style:
:root {
	--primary: #4f46e5;
	--success: #10b981;
	--error: #ef4444;
	--bg: #f8fafc;
	--text: #0f172a;
	--border: #cbd5e1;
}

[data-theme="dark"] {
	--primary: #6366f1;
	--bg: #1e293b;
	--text: #f8fafc;
	--border: #475569;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: 0.3s ease;
}

body {
	font-family: "Inter", system-ui, sans-serif;
	background: var(--bg);
	color: var(--text);
	min-height: 100vh;
	display: grid;
	place-items: center;
	padding: 1rem;
}

.contact-card {
	background: var(--bg);
	width: 100%;
	max-width: 500px;
	padding: 2.5rem;
	border-radius: 1.5rem;
	box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
	border: 1px solid var(--border);
}

.form-header {
	text-align: center;
	margin-bottom: 2rem;
}

.form-header h1 {
	font-size: 2rem;
	margin-bottom: 0.5rem;
}

.form-header i {
	font-size: 2.5rem;
	color: var(--primary);
	margin-bottom: 1rem;
}

.input-group {
	margin-bottom: 1.75rem;
	position: relative;
}

.input-group label {
	position: absolute;
	left: 0.75rem;
	top: 50%;
	transform: translateY(-50%);
	color: #64748b;
	pointer-events: none;
	transition: 0.3s;
}

.input-group input,
.input-group textarea {
	width: 100%;
	padding: 1rem;
	border: 2px solid var(--border);
	border-radius: 0.75rem;
	background: transparent;
	color: var(--text);
}

.input-group textarea {
	min-height: 120px;
	resize: vertical;
}

.input-group input:focus,
.input-group textarea:focus {
	border-color: var(--primary);
	outline: none;
}

.input-group input:not(:placeholder-shown) + label,
.input-group input:focus + label,
.textarea-group textarea:focus + label,
.textarea-group textarea:not(:placeholder-shown) + label {
	top: -0.5rem;
	left: 0.5rem;
	font-size: 0.875rem;
	background: var(--bg);
	padding: 0 0.25rem;
	color: var(--primary);
}

.error-message {
	color: var(--error);
	font-size: 0.875rem;
	margin-top: 0.25rem;
	display: none;
}

button {
	background: var(--primary);
	color: white;
	padding: 1rem 2rem;
	border: none;
	border-radius: 0.75rem;
	font-weight: 600;
	cursor: pointer;
	width: 100%;
	display: flex;
	align-items: center;
	justify-content: center;
	gap: 0.5rem;
}

button:hover {
	opacity: 0.9;
}

.theme-toggle {
	position: fixed;
	top: 1rem;
	right: 1rem;
	max-width: 3rem;
	background: var(--bg);
	border: 2px solid var(--border);
	padding: 0.75rem;
	border-radius: 50%;
	cursor: pointer;
}

@media (max-width: 480px) {
	.contact-card {
		padding: 1.5rem;
	}
}

script:
const themeToggle = document.getElementById("themeToggle");
let isDark = localStorage.getItem("theme") === "dark";

function updateTheme() {
	document.body.setAttribute("data-theme", isDark ? "dark" : "light");
	themeToggle.innerHTML = isDark ? '<i class="fas fa-sun"></i>' : '<i class="fas fa-moon"></i>';
	localStorage.setItem("theme", isDark ? "dark" : "light");
}

themeToggle.addEventListener("click", () => {
	isDark = !isDark;
	updateTheme();
});
updateTheme();
