style:
:root {
  --bg-color: #f5f3f0;
  --text-color: #4a4a4a;
  --border-color: #d4d1cd;
  --accent-color: #a89f95;
  --hover-color: #e8e5e1;
}

[data-theme="dark"] {
  --bg-color: #2d2d2d;
  --text-color: #e8e5e1;
  --border-color: #4a4a4a;
  --accent-color: #7a7268;
  --hover-color: #3d3d3d;
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  transition: all 0.3s ease-in-out;
}

body {
  font-family: "Lato", sans-serif;
  background: var(--bg-color);
  color: var(--text-color);
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
}

header {
  margin: 6rem 0 3rem;
  font-size: 2rem;
  letter-spacing: 4px;
  text-transform: uppercase;
  position: relative;
}

header::after {
  content: "";
  position: absolute;
  bottom: -1rem;
  left: 50%;
  transform: translateX(-50%);
  width: 60px;
  height: 2px;
  background: var(--accent-color);
}

#form {
  width: 100%;
  max-width: 500px;
  margin: 0 auto 4rem;
  padding: 0 1rem;
}

input,
textarea {
  width: 100%;
  padding: 1.2rem;
  background: transparent;
  border: 1px solid var(--border-color);
  margin-bottom: -1px;
  font-size: 0.9rem;
  color: var(--text-color);
  outline: none;
}

input:not([type="submit"]):hover,
textarea:hover {
  background: var(--hover-color);
}

input::placeholder,
textarea::placeholder {
  color: var(--accent-color);
  font-weight: 300;
  letter-spacing: 0.5px;
}

textarea {
  height: 150px;
  resize: vertical;
  margin-bottom: 0;
}

#submit {
  width: 100%;
  padding: 1.2rem;
  background: var(--bg-color);
  border: 1px solid var(--border-color);
  border-top: none;
  color: var(--accent-color);
  text-transform: uppercase;
  letter-spacing: 2px;
  font-weight: 700;
  cursor: pointer;
}

#submit:hover {
  background: var(--hover-color);
  color: var(--text-color);
}

.theme-toggle {
  position: fixed;
  top: 2rem;
  right: 2rem;
  background: transparent;
  border: none;
  color: var(--accent-color);
  cursor: pointer;
  font-size: 1.2rem;
}

@media (max-width: 768px) {
  header {
    margin-top: 4rem;
    font-size: 1.5rem;
  }

  input,
  textarea {
    padding: 1rem;
  }
}

script:
const themeToggle = document.getElementById("themeToggle");
let isDark = localStorage.getItem("theme") === "dark";

function updateTheme() {
  document.body.setAttribute("data-theme", isDark ? "dark" : "light");
  themeToggle.innerHTML = isDark ? '<i class="fas fa-sun"></i>' : '<i class="fas fa-moon"></i>';
  localStorage.setItem("theme", isDark ? "dark" : "light");
}

themeToggle.addEventListener("click", () => {
  isDark = !isDark;
  updateTheme();
});

updateTheme();

document.getElementById("form").addEventListener("submit", (e) => {
  e.preventDefault();
  const submitBtn = document.getElementById("submit");

  const inputs = Array.from(e.target.elements).filter((el) => el.tagName !== "BUTTON");
  const isValid = inputs.every((input) => input.checkValidity());

  if (isValid) {
    submitBtn.value = "SENDING...";
    submitBtn.disabled = true;

    setTimeout(() => {
      submitBtn.value = "SENT!";
      setTimeout(() => {
        submitBtn.value = "SEND";
        submitBtn.disabled = false;
        e.target.reset();
      }, 1500);
    }, 1000);
  }
});
