style:
:root {
--bg: #fafafa;
--surface: #ffffff;
--text: #2d3748;
--primary: #4a5568;
--accent: #718096;
--transition: all 0.2s ease;
}
[data-theme="dark"] {
--bg: #1a202c;
--surface: #2d3748;
--text: #e2e8f0;
--primary: #cbd5e0;
--accent: #a0aec0;
}
* {
margin: 0;
padding: 0;
box-sizing: border-box;
transition: var(--transition);
}
body {
font-family: "Inter", sans-serif;
background: var(--bg);
color: var(--text);
min-height: 100vh;
display: grid;
place-items: center;
padding: 2rem;
}
.form-container {
width: 100%;
max-width: 480px;
padding: 2.5rem;
background: var(--surface);
border-radius: 12px;
box-shadow: 0 10px 25px rgba(0, 0, 0, 0.03);
}
.form-header {
text-align: center;
margin-bottom: 2.5rem;
}
.form-header svg {
width: 48px;
height: 48px;
margin-bottom: 1rem;
color: var(--primary);
}
.form-title {
font-size: 1.8rem;
font-weight: 600;
margin-bottom: 0.5rem;
}
.form-subtitle {
color: var(--accent);
font-size: 0.95rem;
}
.input-group {
margin-bottom: 1.5rem;
}
.input-row {
display: grid;
grid-template-columns: 1fr 1fr;
gap: 1rem;
}
input, textarea {
width: 100%;
padding: 0.9rem 1.2rem;
border: 1px solid var(--accent);
border-radius: 8px;
background: transparent;
color: var(--text);
font-size: 0.95rem;
}
input:focus, textarea:focus {
outline: none;
border-color: var(--primary);
box-shadow: 0 0 0 3px rgba(113, 128, 150, 0.1);
}
textarea {
min-height: 120px;
resize: vertical;
}
button {
width: 100%;
padding: 1rem;
background: var(--primary);
color: var(--surface);
border: none;
border-radius: 8px;
font-weight: 500;
cursor: pointer;
display: flex;
align-items: center;
justify-content: center;
gap: 0.5rem;
}
button:hover {
opacity: 0.9;
}
.theme-toggle {
position: fixed;
top: 1.5rem;
right: 1.5rem;
background: var(--surface);
border: 1px solid var(--accent);
padding: 0.75rem;
border-radius: 8px;
cursor: pointer;
  max-width: 5rem
}
@media (max-width: 640px) {
.input-row {
grid-template-columns: 1fr;
}
.form-container {
padding: 2rem 1.5rem;
}
}

script:
const themeToggle = document.getElementById("themeToggle");
let isDark = localStorage.getItem("theme") === "dark";

function updateTheme() {
document.body.setAttribute("data-theme", isDark ? "dark" : "light");
localStorage.setItem("theme", isDark ? "dark" : "light");
}

themeToggle.addEventListener("click", () => {
isDark = !isDark;
updateTheme();
});

updateTheme();

document.querySelector("form").addEventListener("submit", (e) => {
e.preventDefault();
const btn = e.target.querySelector("button");
btn.innerHTML = "Sending...";
btn.disabled = true;

setTimeout(() => {
btn.innerHTML = "Message Sent!";
setTimeout(() => {
btn.innerHTML = "Send Message";
btn.disabled = false;
e.target.reset();
}, 1500);
}, 1000);
});
