style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
}

.cta-section {
	padding: 80px 20px;
	background: #f8f9fa;
}

.cta-container {
	max-width: 1200px;
	margin: 0 auto;
	display: flex;
	align-items: center;
	gap: 50px;
	flex-wrap: wrap;
	justify-content: center;
}

.cta-content {
	flex: 1;
	min-width: 300px;
}

.cta-image {
	flex: 1;
	min-width: 300px;
	border-radius: 15px;
	overflow: hidden;
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
	transition: transform 0.3s ease;
}

.cta-image img {
	width: 100%;
	height: auto;
	display: block;
}

.cta-title {
	font-size: 2.5rem;
	color: #2d3436;
	margin-bottom: 20px;
	line-height: 1.2;
}

.cta-text {
	color: #636e72;
	margin-bottom: 30px;
	font-size: 1.1rem;
	line-height: 1.6;
}

.cta-button {
	display: inline-block;
	padding: 15px 40px;
	background: linear-gradient(135deg, #6c5ce7, #a66efa);
	color: white;
	text-decoration: none;
	border-radius: 30px;
	font-weight: 600;
	transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.cta-button:hover {
	transform: translateY(-3px);
	box-shadow: 0 10px 20px rgba(108, 92, 231, 0.3);
}

@media (max-width: 768px) {
	.cta-container {
		flex-direction: column;
		text-align: center;
	}

	.cta-title {
		font-size: 2rem;
	}

	.cta-image {
		max-width: 500px;
	}
}

@media (max-width: 480px) {
	.cta-title {
		font-size: 1.75rem;
	}

	.cta-text {
		font-size: 1rem;
	}

	.cta-button {
		width: 100%;
		padding: 15px 20px;
	}
}

script:
// Add hover effect on image
const ctaImage = document.querySelector(".cta-image");

ctaImage.addEventListener("mouseover", () => {
	ctaImage.style.transform = "translateY(-5px)";
});

ctaImage.addEventListener("mouseout", () => {
	ctaImage.style.transform = "translateY(0)";
});

// Smooth scroll for button (example)
document.querySelector(".cta-button").addEventListener("click", (e) => {
	e.preventDefault();
	// Add your click handling logic here
	alert("Button clicked!");
});
