style:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  font-family: "Inter", sans-serif;
}

.cta-section {
  background: linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.7)),
    url("https://picsum.photos/1920/1080") center/cover fixed;
  padding: 120px 20px;
  color: white;
  text-align: center;
  position: relative;
  overflow: hidden;
}

.cta-container {
  max-width: 1200px;
  margin: 0 auto;
  position: relative;
  z-index: 1;
}

.cta-tagline {
  color: #ffd700;
  text-transform: uppercase;
  letter-spacing: 2px;
  margin-bottom: 15px;
  display: block;
  font-weight: 500;
}

.cta-title {
  font-size: 3rem;
  margin-bottom: 25px;
  line-height: 1.2;
  text-shadow: 0 2px 4px rgba(0, 0, 0, 0.3);
}

.cta-button {
  display: inline-flex;
  align-items: center;
  padding: 18px 50px;
  background: #007bff;
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 1.1rem;
  cursor: pointer;
  transition: all 0.3s ease;
  gap: 12px;
  text-decoration: none;
  margin: 25px 0;
}

.cta-button:hover {
  background: #0056b3;
  transform: translateY(-3px);
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
}

.highlights {
  display: flex;
  justify-content: center;
  gap: 50px;
  margin-top: 60px;
  flex-wrap: wrap;
}

.highlight-item {
  text-align: center;
  max-width: 250px;
}

.highlight-icon {
  width: 60px;
  height: 60px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0 auto 20px;
  transition: all 0.3s ease;
}

.highlight-icon svg {
  color: #ffd700;
}

.highlight-title {
  font-size: 1.3rem;
  margin-bottom: 10px;
}

.highlight-text {
  color: #e0e0e0;
  font-size: 0.95rem;
  line-height: 1.6;
}

@media (max-width: 768px) {
  .cta-section {
    padding: 80px 20px;
  }

  .cta-title {
    font-size: 2rem;
  }

  .highlights {
    gap: 30px;
    margin-top: 40px;
  }
}

@media (max-width: 480px) {
  .cta-title {
    font-size: 1.8rem;
  }

  .cta-button {
    width: 100%;
    justify-content: center;
  }
}

script:
document.addEventListener("DOMContentLoaded", function () {
  const ctaSection = document.querySelector(".cta-section");
  const randomImageId = Math.floor(Math.random() * 1000);
  ctaSection.style.backgroundImage = `linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.7)),
   url('https://picsum.photos/id/${randomImageId}/1920/1080')`;
});

// Smooth scroll for CTA button
document.querySelector(".cta-button").addEventListener("click", function (e) {
  e.preventDefault();
  const target = document.querySelector(this.getAttribute("href"));
  if (target) {
    target.scrollIntoView({
      behavior: "smooth",
    });
  }
});

// Animation for highlight items
const highlights = document.querySelectorAll(".highlight-item");
const observer = new IntersectionObserver(
  (entries) => {
    entries.forEach((entry) => {
      if (entry.isIntersecting) {
        entry.target.style.opacity = 1;
        entry.target.style.transform = "translateY(0)";
      }
    });
  },
  { threshold: 0.1 }
);

highlights.forEach((item) => {
  item.style.opacity = 0;
  item.style.transform = "translateY(30px)";
  item.style.transition = "all 0.6s ease";
  observer.observe(item);
});

// Hover effect for highlight icons
document.querySelectorAll(".highlight-icon").forEach((icon) => {
  icon.addEventListener("mouseover", () => {
    icon.style.transform = "scale(1.1) rotate(5deg)";
    icon.style.background = "rgba(255, 255, 255, 0.15)";
  });

  icon.addEventListener("mouseout", () => {
    icon.style.transform = "scale(1) rotate(0)";
    icon.style.background = "rgba(255, 255, 255, 0.1)";
  });
});
