style:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  font-family: "Inter", sans-serif;
}

.cta-section {
  padding: 100px 20px;
  background: #f8f9fa;
}

.cta-container {
  max-width: 1200px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1.2fr 1fr;
  gap: 60px;
  align-items: center;
}

.cta-image {
  border-radius: 20px;
  overflow: hidden;
  box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.15);
  position: relative;
}

.cta-image img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  min-height: 400px;
}

.cta-content {
  padding: 40px;
  position: relative;
}

.cta-badge {
  position: absolute;
  top: -20px;
  left: 40px;
  background: #e74c3c;
  color: white;
  padding: 12px 25px;
  border-radius: 30px;
  font-weight: 600;
  box-shadow: 0 10px 20px rgba(231, 76, 60, 0.3);
}

.cta-title {
  font-size: 2.8rem;
  color: #2d3436;
  margin-bottom: 25px;
  line-height: 1.1;
}

.cta-text {
  font-size: 1.1rem;
  color: #636e72;
  line-height: 1.7;
  margin-bottom: 35px;
}

.cta-button {
  display: inline-flex;
  align-items: center;
  gap: 12px;
  padding: 18px 45px;
  background: #0984e3;
  color: white;
  border: none;
  border-radius: 12px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  text-decoration: none;
}

.cta-button:hover {
  background: #0873c4;
  transform: translateY(-3px);
  box-shadow: 0 10px 20px rgba(9, 132, 227, 0.2);
}

.stats-container {
  display: flex;
  gap: 30px;
  margin-top: 40px;
  padding-top: 30px;
  border-top: 2px solid #eee;
}

.stat-item {
  text-align: center;
}

.stat-number {
  font-size: 2rem;
  font-weight: 700;
  color: #2d3436;
  margin-bottom: 5px;
}

.stat-label {
  color: #636e72;
  font-size: 0.9rem;
}

@media (max-width: 1024px) {
  .cta-container {
    grid-template-columns: 1fr;
    gap: 40px;
  }

  .cta-image {
    max-width: 600px;
    margin: 0 auto;
  }

  .cta-content {
    text-align: center;
    padding: 0 20px;
  }

  .cta-badge {
    left: 50%;
    transform: translateX(-50%);
  }

  .stats-container {
    justify-content: center;
    flex-wrap: wrap;
  }
}

@media (max-width: 768px) {
  .cta-title {
    font-size: 2rem;
  }

  .cta-text {
    font-size: 1rem;
  }

  .stat-number {
    font-size: 1.5rem;
  }
}

script:
const observer = new IntersectionObserver((entries) => {
  entries.forEach((entry) => {
    if (entry.isIntersecting) {
      entry.target.style.opacity = 1;
      entry.target.style.transform = "translateY(0)";
    }
  });
});

const elementsToAnimate = document.querySelectorAll(".cta-image, .cta-content");

elementsToAnimate.forEach((el) => {
  el.style.opacity = 0;
  el.style.transform = "translateY(30px)";
  el.style.transition = "all 0.6s cubic-bezier(0.4, 0, 0.2, 1)";
  observer.observe(el);
});

const button = document.querySelector(".cta-button");
button.addEventListener("mousemove", (e) => {
  const rect = button.getBoundingClientRect();
  const x = e.clientX - rect.left;
  const y = e.clientY - rect.top;
  button.style.transform = `translateY(-3px) translate(${x * 0.05}px, ${y * 0.05}px)`;
});
button.addEventListener("mouseleave", () => {
  button.style.transform = "translateY(0)";
});
