style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", sans-serif;
}

.cta-section {
	background: #0f172a;
	padding: 100px 20px;
	position: relative;
	overflow: hidden;
}

.cta-container {
	max-width: 1200px;
	margin: 0 auto;
	display: grid;
	grid-template-columns: 1fr 1fr;
	gap: 60px;
	align-items: center;
}

.content-column {
	position: relative;
	z-index: 2;
}

.image-column {
	position: relative;
	border-radius: 20px;
	overflow: hidden;
	height: 600px;
}

.cta-image {
	width: 100%;
	height: 100%;
	object-fit: cover;
	opacity: 0.9;
}

.cta-tagline {
	color: #2dd4bf;
	text-transform: uppercase;
	letter-spacing: 2px;
	font-weight: 600;
	margin-bottom: 20px;
	display: block;
}

.cta-title {
	font-size: 2.8rem;
	color: #f8fafc;
	margin-bottom: 25px;
	line-height: 1.2;
}

.cta-text {
	color: #94a3b8;
	font-size: 1.1rem;
	line-height: 1.8;
	margin-bottom: 40px;
}

.stats-grid {
	display: grid;
	grid-template-columns: repeat(3, 1fr);
	gap: 30px;
	margin-bottom: 50px;
}

.stat-item {
	text-align: center;
	padding: 20px;
	background: rgba(45, 212, 191, 0.1);
	border-radius: 12px;
	border: 1px solid rgba(45, 212, 191, 0.2);
}

.stat-number {
	color: #2dd4bf;
	font-size: 2.2rem;
	font-weight: 700;
	margin-bottom: 8px;
}

.stat-text {
	color: #cbd5e1;
	font-size: 0.9rem;
}

.cta-button {
	display: inline-flex;
	align-items: center;
	padding: 18px 45px;
	background: #2dd4bf;
	color: #0f172a;
	border: none;
	border-radius: 12px;
	font-weight: 700;
	cursor: pointer;
	transition: all 0.3s ease;
	text-decoration: none;
	gap: 12px;
	font-size: 1.1rem;
}

.cta-button:hover {
	background: #22c8b5;
	transform: translateY(-3px);
	box-shadow: 0 10px 20px rgba(45, 212, 191, 0.3);
}

.gradient-overlay {
	position: absolute;
	top: -500px;
	right: -500px;
	width: 1000px;
	height: 1000px;
	background: radial-gradient(circle, rgba(45, 212, 191, 0.15) 0%, transparent 70%);
	pointer-events: none;
}

@media (max-width: 1024px) {
	.cta-container {
		grid-template-columns: 1fr;
		gap: 40px;
	}

	.image-column {
		height: 400px;
	}

	.cta-title {
		font-size: 2.2rem;
	}
}

@media (max-width: 768px) {
	.stats-grid {
		grid-template-columns: 1fr;
	}

	.cta-button {
		width: 100%;
		justify-content: center;
	}
}

script:
document.addEventListener("DOMContentLoaded", function () {
	const nutritionKeywords = ["meal-prep", "superfood", "smoothie-bowl", "vegetables", "healthy-eating"];
	const randomKeyword = nutritionKeywords[Math.floor(Math.random() * nutritionKeywords.length)];
	const image = document.getElementById("ctaImage");
	image.src = `https://picsum.photos/800/600?${randomKeyword}`;
});

const stats = document.querySelectorAll(".stat-item");
const observer = new IntersectionObserver((entries) => {
	entries.forEach((entry) => {
		if (entry.isIntersecting) {
			entry.target.style.opacity = 1;
			entry.target.style.transform = "translateY(0)";
		}
	});
});

stats.forEach((stat) => {
	stat.style.opacity = 0;
	stat.style.transform = "translateY(20px)";
	stat.style.transition = "all 0.6s ease";
	observer.observe(stat);
});
