style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", sans-serif;
}

.cta-section {
	padding: 100px 20px;
	background: #fff5e6;
}

.cta-container {
	max-width: 1200px;
	margin: 0 auto;
	display: grid;
	grid-template-columns: 1.5fr 1fr;
	gap: 40px;
	align-items: center;
}

.cta-content {
	background: rgba(255, 255, 255, 0.95);
	padding: 60px;
	border-radius: 20px;
	box-shadow: 0 20px 40px rgba(0, 0, 0, 0.08);
	position: relative;
	z-index: 2;
}

.cta-image {
	position: relative;
	height: 500px;
	border-radius: 20px;
	overflow: hidden;
	transform: rotate(-3deg);
}

.cta-image img {
	width: 100%;
	height: 100%;
	object-fit: cover;
	transition: transform 0.5s ease;
}

.cta-image:hover img {
	transform: scale(1.05);
}

.seasonal-badge {
	background: #e67e22;
	color: white;
	padding: 8px 20px;
	border-radius: 30px;
	position: absolute;
	top: -15px;
	left: 50%;
	transform: translateX(-50%);
	font-weight: 600;
	box-shadow: 0 10px 20px rgba(230, 126, 34, 0.3);
}

.cta-title {
	font-size: 2.8rem;
	color: #2d3436;
	margin-bottom: 25px;
	line-height: 1.1;
}

.cta-text {
	font-size: 1.1rem;
	color: #636e72;
	line-height: 1.7;
	margin-bottom: 35px;
}

.cta-button {
	display: inline-flex;
	align-items: center;
	gap: 12px;
	padding: 18px 45px;
	background: #e67e22;
	color: white;
	border: none;
	border-radius: 12px;
	font-weight: 600;
	cursor: pointer;
	transition: all 0.3s ease;
	text-decoration: none;
}

.cta-button:hover {
	background: #d35400;
	transform: translateY(-3px);
	box-shadow: 0 10px 20px rgba(230, 126, 34, 0.2);
}

.highlights {
	display: flex;
	gap: 30px;
	margin-top: 40px;
}

.highlight-item {
	text-align: center;
	flex: 1;
}

.highlight-icon {
	width: 40px;
	height: 40px;
	margin-bottom: 10px;
	color: #e67e22;
}

@media (max-width: 1024px) {
	.cta-container {
		grid-template-columns: 1fr;
	}

	.cta-content {
		margin: 0 auto;
		max-width: 600px;
	}

	.cta-image {
		height: 400px;
		order: -1;
		transform: rotate(0);
	}

	.cta-title {
		font-size: 2.2rem;
	}
}

@media (max-width: 768px) {
	.cta-content {
		padding: 40px 25px;
	}

	.cta-title {
		font-size: 2rem;
	}

	.highlights {
		flex-direction: column;
		gap: 25px;
	}
}

script:
const observer = new IntersectionObserver((entries) => {
	entries.forEach((entry) => {
		if (entry.isIntersecting) {
			entry.target.style.opacity = 1;
			entry.target.style.transform = "translateY(0)";
		}
	});
});

const animatedElements = document.querySelectorAll(".cta-image, .cta-content");
animatedElements.forEach((el) => {
	el.style.opacity = 0;
	el.style.transform = "translateY(30px)";
	el.style.transition = "all 0.6s ease-out";
	observer.observe(el);
});

const image = document.querySelector(".cta-image");
image.addEventListener("mousemove", (e) => {
	const rect = image.getBoundingClientRect();
	const x = e.clientX - rect.left;
	const y = e.clientY - rect.top;
	image.style.transform = `rotate(-3deg) translate(${x * 0.02}px, ${y * 0.02}px)`;
});
image.addEventListener("mouseleave", () => {
	image.style.transform = "rotate(-3deg) translate(0, 0)";
});
