style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", sans-serif;
}

.cta-section {
	padding: 100px 20px;
	background: linear-gradient(45deg, #fff5f5 0%, #fff0f7 100%);
}

.cta-container {
	max-width: 1200px;
	margin: 0 auto;
	display: grid;
	grid-template-columns: 1.2fr 1fr;
	gap: 60px;
	align-items: center;
}

.image-column {
	border-radius: 20px;
	overflow: hidden;
	height: 600px;
	box-shadow: 0 25px 50px -12px rgba(255, 101, 165, 0.2);
}

.cta-image {
	width: 100%;
	height: 100%;
	object-fit: cover;
}

.content-column {
	padding: 40px 0;
}

.cta-subtitle {
	color: #ff65a5;
	text-transform: uppercase;
	letter-spacing: 2px;
	font-weight: 600;
	margin-bottom: 15px;
}

.cta-title {
	font-size: 2.8rem;
	color: #2d3436;
	margin-bottom: 25px;
	line-height: 1.2;
}

.cta-text {
	color: #636e72;
	font-size: 1.1rem;
	line-height: 1.8;
	margin-bottom: 35px;
}

.benefits-list {
	list-style: none;
	margin-bottom: 40px;
}

.benefit-item {
	display: flex;
	align-items: center;
	gap: 15px;
	margin-bottom: 20px;
	padding: 15px;
	background: rgba(255, 255, 255, 0.9);
	border-radius: 12px;
	box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
}

.benefit-icon {
	width: 40px;
	height: 40px;
	background: #ffe5ef;
	border-radius: 10px;
	display: flex;
	align-items: center;
	justify-content: center;
	flex-shrink: 0;
}

.benefit-text strong {
	color: #ff65a5;
	display: block;
	margin-bottom: 5px;
}

.cta-button {
	display: inline-flex;
	align-items: center;
	padding: 18px 45px;
	background: #ff65a5;
	color: white;
	border: none;
	border-radius: 15px;
	font-weight: 600;
	cursor: pointer;
	transition: all 0.3s ease;
	text-decoration: none;
	gap: 12px;
	font-size: 1.1rem;
}

.cta-button:hover {
	background: #ff4d94;
	transform: translateY(-3px);
	box-shadow: 0 10px 20px rgba(255, 101, 165, 0.3);
}

@media (max-width: 1024px) {
	.cta-container {
		grid-template-columns: 1fr;
		gap: 40px;
	}

	.image-column {
		height: 400px;
		order: -1;
	}
}

@media (max-width: 768px) {
	.cta-title {
		font-size: 2rem;
	}

	.cta-button {
		width: 100%;
		justify-content: center;
	}
}

script:
document.addEventListener("DOMContentLoaded", function () {
	const wellnessKeywords = ["yoga", "meditation", "healthy", "smoothie", "fitness"];
	const randomKeyword = wellnessKeywords[Math.floor(Math.random() * wellnessKeywords.length)];
	const image = document.getElementById("ctaImage");
	image.src = `https://picsum.photos/800/600?${randomKeyword}`;
});
