style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", sans-serif;
}

.cta-section {
	min-height: 100vh;
	display: grid;
	grid-template-columns: 1fr 1fr;
	position: relative;
}

.content-side {
	background: #2c3e50;
	color: white;
	padding: 80px;
	display: flex;
	flex-direction: column;
	justify-content: center;
	position: relative;
	overflow: hidden;
}

.image-side {
	background: linear-gradient(rgba(0, 0, 0, 0.2), rgba(0, 0, 0, 0.2)),
		url("https://picsum.photos/800/1200?cuisine") center/cover;
}

.cta-tag {
	background: #e74c3c;
	color: white;
	padding: 8px 20px;
	border-radius: 30px;
	position: absolute;
	top: 40px;
	left: -15px;
	transform: rotate(-90deg);
	font-weight: 600;
}

.cta-title {
	font-size: 3.2rem;
	margin-bottom: 30px;
	line-height: 1.1;
}

.cta-text {
	font-size: 1.1rem;
	color: #bdc3c7;
	line-height: 1.8;
	margin-bottom: 40px;
	max-width: 500px;
}

.cta-button {
	display: inline-flex;
	align-items: center;
	gap: 12px;
	padding: 18px 45px;
	margin-top: 24px;
	background: #e74c3c;
	color: white;
	border: none;
	border-radius: 8px;
	font-weight: 600;
	cursor: pointer;
	transition: all 0.3s ease;
	text-decoration: none;
	width: fit-content;
}

.cta-button:hover {
	background: #c0392b;
	transform: translateY(-3px);
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.2);
}

.features-grid {
	display: grid;
	grid-template-columns: repeat(2, 1fr);
	gap: 30px;
	margin-top: 60px;
}

.feature-item {
	display: flex;
	gap: 15px;
	align-items: center;
}

.feature-icon {
	width: 40px;
	height: 40px;
	color: #e74c3c;
}

@media (max-width: 1024px) {
	.cta-section {
		grid-template-columns: 1fr;
		min-height: auto;
	}

	.content-side {
		padding: 60px 40px;
	}

	.image-side {
		height: 500px;
		order: -1;
	}

	.cta-tag {
		top: 20px;
		left: 50%;
		transform: translateX(-50%) rotate(0);
	}

	.cta-title {
		font-size: 2.5rem;
	}
}

@media (max-width: 768px) {
	.cta-title {
		font-size: 2rem;
	}

	.features-grid {
		grid-template-columns: 1fr;
	}

	.content-side {
		padding: 40px 25px;
	}
}

script:
// Intersection Observer for scroll animations
const observer = new IntersectionObserver((entries) => {
	entries.forEach((entry) => {
		if (entry.isIntersecting) {
			entry.target.style.opacity = 1;
			entry.target.style.transform = "translateY(0)";
		}
	});
});

// Animate elements
const elementsToAnimate = document.querySelectorAll(".content-side, .image-side");
	elementsToAnimate.forEach((el) => {
		el.style.opacity = 0;
		el.style.transform = "translateY(30px)";
		el.style.transition = "all 0.6s cubic-bezier(0.4, 0, 0.2, 1)";
		observer.observe(el);
});

// Parallax effect on image side
window.addEventListener("scroll", () => {
	const imageSide = document.querySelector(".image-side");
	const scrollPosition = window.pageYOffset;
	imageSide.style.backgroundPositionY = `${scrollPosition * 0.5}px`;
});
