style:
 * {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", system-ui, -apple-system, sans-serif;
}

.cta-section {
	padding: 120px 20px;
	background: #f0f4ff;
	position: relative;
}

.cta-container {
	max-width: 1200px;
	margin: 0 auto;
	display: flex;
	gap: 40px;
	align-items: center;
	position: relative;
}

.cta-card {
	background: white;
	border-radius: 24px;
	padding: 60px;
	box-shadow: 0 32px 64px -16px rgba(67, 97, 238, 0.15);
	position: relative;
	overflow: hidden;
	flex: 1;
}

.cta-card::before {
	content: "";
	position: absolute;
	top: -50px;
	right: -50px;
	width: 150px;
	height: 150px;
	background: #4361ee;
	border-radius: 50%;
	opacity: 0.1;
}

.cta-card::after {
	content: "";
	position: absolute;
	bottom: -80px;
	left: -40px;
	width: 200px;
	height: 200px;
	background: #4361ee;
	border-radius: 50%;
	opacity: 0.05;
}

.cta-title {
	font-size: 2.8rem;
	color: #1a237e;
	margin-bottom: 25px;
	line-height: 1.1;
	position: relative;
	z-index: 2;
}

.cta-text {
	font-size: 1.1rem;
	color: #4a5568;
	line-height: 1.7;
	margin-bottom: 35px;
	max-width: 500px;
	position: relative;
	z-index: 2;
}

.cta-button {
	display: inline-flex;
	align-items: center;
	gap: 12px;
	padding: 18px 45px;
	background: #4361ee;
	color: white;
	text-decoration: none;
	border-radius: 14px;
	font-weight: 600;
	transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
	position: relative;
	z-index: 2;
	border: 2px solid transparent;
}

.cta-button:hover {
	background: #364fc7;
	transform: translateY(-2px);
	box-shadow: 0 10px 20px rgba(67, 97, 238, 0.2);
}

.cta-image {
	flex: 1;
	min-width: 400px;
	position: relative;
	border-radius: 20px;
	overflow: hidden;
	transform: rotate(3deg);
	box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.1);
}

.cta-image img {
	width: 100%;
	height: 100%;
	object-fit: cover;
	transform: rotate(-3deg) scale(1.05);
}

@media (max-width: 1024px) {
	.cta-container {
		flex-direction: column;
		text-align: center;
	}

	.cta-card {
		padding: 40px;
	}

	.cta-title {
		font-size: 2.2rem;
	}

	.cta-text {
		margin: 0 auto 35px;
	}

	.cta-image {
		min-width: auto;
		width: 100%;
		max-width: 600px;
	}
}

@media (max-width: 640px) {
	.cta-section {
		padding: 80px 20px;
	}

	.cta-title {
		font-size: 1.8rem;
	}

	.cta-text {
		font-size: 1rem;
	}

	.cta-button {
		width: 100%;
		justify-content: center;
	}
}

script:
// Animate elements on scroll
const animateOnScroll = () => {
	const cards = document.querySelectorAll(".cta-card, .cta-image");
	cards.forEach((card) => {
		const cardTop = card.getBoundingClientRect().top;
		const windowHeight = window.innerHeight;

		if (cardTop < windowHeight * 0.8) {
			card.style.opacity = "1";
			card.style.transform = "translateY(0)";
		}
	});
};

// Initial hide elements
document.querySelectorAll(".cta-card, .cta-image").forEach((el) => {
	el.style.opacity = "0";
	el.style.transform = "translateY(30px)";
	el.style.transition = "all 0.6s cubic-bezier(0.4, 0, 0.2, 1)";
});

// Event listeners
window.addEventListener("scroll", animateOnScroll);
window.addEventListener("load", animateOnScroll);

// Hover effect for card
const card = document.querySelector(".cta-card");
card.addEventListener("mousemove", (e) => {
	const rect = card.getBoundingClientRect();
	const x = e.clientX - rect.left;
	const y = e.clientY - rect.top;

	card.style.transform = `
		translateY(-2px)
		rotateX(${(y - rect.height / 2) / 15}deg)
		rotateY(${-(x - rect.width / 2) / 15}deg)
	`;
});

card.addEventListener("mouseleave", () => {
	card.style.transform = "translateY(0) rotateX(0) rotateY(0)";
});
