style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
}

.cta-section {
	background: #f8f9fa;
	padding: 80px 20px;
}

.cta-container {
	max-width: 1200px;
	margin: 0 auto;
	display: grid;
	grid-template-columns: 1fr 1fr;
	gap: 50px;
	align-items: center;
}

.cta-content {
	padding-right: 30px;
}

.cta-title {
	font-size: 2.5rem;
	color: #2d3436;
	margin-bottom: 20px;
	line-height: 1.2;
}

.cta-text {
	font-size: 1.1rem;
	color: #636e72;
	margin-bottom: 30px;
	line-height: 1.6;
}

.cta-button {
	display: inline-block;
	padding: 15px 40px;
	background: linear-gradient(45deg, #6c5ce7, #a66efa);
	color: white;
	text-decoration: none;
	border-radius: 30px;
	font-weight: 600;
	transition: transform 0.3s ease;
}

.cta-button:hover {
	transform: translateY(-3px);
}

.cta-image {
	width: 100%;
	height: 400px;
	border-radius: 20px;
	overflow: hidden;
	box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.1);
}

.cta-image img {
	width: 100%;
	height: 100%;
	object-fit: cover;
}

@media (max-width: 768px) {
	.cta-container {
		grid-template-columns: 1fr;
		gap: 30px;
	}

	.cta-content {
		padding-right: 0;
		text-align: center;
	}

	.cta-title {
		font-size: 2rem;
	}

	.cta-image {
		height: 300px;
	}
}

@media (max-width: 480px) {
	.cta-title {
		font-size: 1.75rem;
	}

	.cta-button {
		width: 100%;
		text-align: center;
	}
}

script:
document.addEventListener("DOMContentLoaded", function () {
	const image = document.getElementById("ctaImage");
	const randomId = Math.floor(Math.random() * 1000);
	image.src = `https://picsum.photos/id/${randomId}/600/400`;
});

// Add smooth scroll for the CTA button

document.querySelector(".cta-button").addEventListener("click", function (e) {
	e.preventDefault();
	const target = document.querySelector(this.getAttribute("href"));
	if (target) {
		target.scrollIntoView({
			behavior: "smooth",
		});
	}
});

// Add image hover effect
const ctaImage = document.querySelector(".cta-image");
ctaImage.addEventListener("mouseover", () => {
	ctaImage.style.transform = "scale(1.02)";
	ctaImage.style.transition = "transform 0.3s ease";
});

ctaImage.addEventListener("mouseout", () => {
	ctaImage.style.transform = "scale(1)";
});
