style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", system-ui, -apple-system, sans-serif;
}

.cta-section {
	background: linear-gradient(135deg, rgba(40, 42, 55, 0.95) 0%, rgba(40, 42, 55, 0.9) 100%);
	padding: 100px 20px;
	position: relative;
	overflow: hidden;
}

.cta-bg-image {
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	z-index: -1;
	opacity: 0.15;
}

.cta-bg-image img {
	width: 100%;
	height: 100%;
	object-fit: cover;
}

.cta-container {
	max-width: 1200px;
	margin: 0 auto;
	text-align: center;
	color: white;
}

.cta-tagline {
	font-size: 1.1rem;
	color: #a66efa;
	text-transform: uppercase;
	letter-spacing: 2px;
	margin-bottom: 15px;
}

.cta-title {
	font-size: 2.8rem;
	margin-bottom: 25px;
	line-height: 1.2;
}

.cta-text {
	font-size: 1.2rem;
	color: #e0e0e0;
	max-width: 800px;
	margin: 0 auto 40px;
	line-height: 1.6;
}

.cta-button {
	display: inline-flex;
	align-items: center;
	padding: 16px 45px;
	background: #6c5ce7;
	color: white;
	text-decoration: none;
	border-radius: 12px;
	font-weight: 600;
	transition: all 0.3s ease;
	gap: 10px;
}

.cta-button:hover {
	background: #7b6cf0;
	transform: translateY(-2px);
	box-shadow: 0 10px 20px rgba(108, 92, 231, 0.3);
}

.stats-container {
	display: flex;
	justify-content: center;
	gap: 50px;
	margin-top: 60px;
	flex-wrap: wrap;
}

.stat-item {
	text-align: center;
}

.stat-number {
	font-size: 2.5rem;
	font-weight: 700;
	color: #a66efa;
	margin-bottom: 5px;
}

.stat-label {
	color: #c4c4c4;
	text-transform: uppercase;
	letter-spacing: 1px;
	font-size: 0.9rem;
}

@media (max-width: 768px) {
	.cta-title {
		font-size: 2rem;
	}

	.cta-text {
		font-size: 1rem;
	}

	.cta-button {
		width: 100%;
		justify-content: center;
	}

	.stats-container {
		gap: 30px;
		margin-top: 40px;
	}

	.stat-number {
		font-size: 2rem;
	}
}

@media (max-width: 480px) {
	.cta-section {
		padding: 60px 20px;
	}

	.cta-title {
		font-size: 1.75rem;
	}

	.stats-container {
		gap: 20px;
	}
}

script:
document.addEventListener("DOMContentLoaded", function () {
	const bgImage = document.getElementById("ctaBg");
	const randomImageId = Math.floor(Math.random() * 1000);
	bgImage.src = `https://picsum.photos/id/${randomImageId}/1920/1080`;
});

const statsItems = document.querySelectorAll(".stat-item");
const observerOptions = {
	threshold: 0.5,
	rootMargin: "0px",
};

const observer = new IntersectionObserver((entries) => {
	entries.forEach((entry) => {
		if (entry.isIntersecting) {
			entry.target.style.opacity = 1;
			entry.target.style.transform = "translateY(0)";
		}
	});
}, observerOptions);

statsItems.forEach((item) => {
	item.style.opacity = 0;
	item.style.transform = "translateY(20px)";
	item.style.transition = "all 0.6s ease";
	observer.observe(item);
});

const ctaButton = document.querySelector(".cta-button");
ctaButton.addEventListener("mousemove", (e) => {
	const rect = ctaButton.getBoundingClientRect();
	const x = e.clientX - rect.left;
	const y = e.clientY - rect.top;
	ctaButton.style.setProperty("--x", `${x}px`);
	ctaButton.style.setProperty("--y", `${y}px`);
});
