style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", system-ui, -apple-system, sans-serif;
}

.cta-section {
	min-height: 100vh;
	display: grid;
	grid-template-columns: 1fr 1fr;
	position: relative;
}

.cta-content {
	padding: 80px;
	background: #0f172a;
	color: white;
	display: flex;
	flex-direction: column;
	justify-content: center;
	position: relative;
	overflow: hidden;
}

.cta-image {
	background: linear-gradient(rgba(15, 23, 42, 0.4), rgba(15, 23, 42, 0.4)),
		url("https://picsum.photos/1200/800");
	background-size: cover;
	background-position: center;
	position: relative;
	display: flex;
	align-items: flex-end;
	padding: 60px;
}

.cta-title {
	font-size: 3rem;
	margin-bottom: 30px;
	line-height: 1.1;
	position: relative;
	z-index: 2;
}

.cta-text {
	font-size: 1.1rem;
	color: #94a3b8;
	line-height: 1.8;
	margin-bottom: 40px;
	max-width: 500px;
}

.cta-form {
	max-width: 500px;
}

.email-input {
	width: 100%;
	padding: 18px 25px;
	border-radius: 12px;
	border: 2px solid #334155;
	background: #1e293b;
	color: white;
	font-size: 1rem;
	transition: all 0.3s ease;
}

.email-input:focus {
	outline: none;
	border-color: #6366f1;
	box-shadow: 0 0 0 3px rgba(99, 102, 241, 0.2);
}

.cta-button {
	margin-top: 20px;
	width: 100%;
	padding: 18px;
	background: #6366f1;
	color: white;
	border: none;
	border-radius: 12px;
	font-weight: 600;
	cursor: pointer;
	transition: all 0.3s ease;
}

.cta-button:hover {
	background: #4f46e5;
	transform: translateY(-2px);
}

.decorative-line {
	position: absolute;
	background: #6366f1;
	height: 4px;
	width: 100px;
	top: 50%;
	left: -50px;
	transform: translateY(-50%);
}

@media (max-width: 1024px) {
	.cta-section {
		grid-template-columns: 1fr;
	}

	.cta-content {
		padding: 60px 40px;
	}

	.cta-image {
		padding: 40px;
		min-height: 400px;
	}

	.cta-title {
		font-size: 2.5rem;
	}
}

@media (max-width: 640px) {
	.cta-title {
		font-size: 2rem;
	}

	.cta-text {
		font-size: 1rem;
	}

	.cta-content,
	.cta-image {
		padding: 40px 20px;
	}
}

script:
window.addEventListener("scroll", () => {
	const imageSection = document.querySelector(".cta-image");
	const scrollPosition = window.pageYOffset;
	imageSection.style.backgroundPositionY = `${scrollPosition * 0.5}px`;
});

// Input animation
const emailInput = document.querySelector(".email-input");
emailInput.addEventListener("focus", () => {
	emailInput.parentElement.style.transform = "translateY(-5px)";
});
emailInput.addEventListener("blur", () => {
	emailInput.parentElement.style.transform = "translateY(0)";
});

// Intersection Observer for fade-in effect
const observer = new IntersectionObserver((entries) => {
	entries.forEach((entry) => {
		if (entry.isIntersecting) {
			entry.target.style.opacity = 1;
			entry.target.style.transform = "translateY(0)";
		}
	});
});

document.querySelectorAll(".cta-content, .image-content").forEach((el) => {
	el.style.opacity = 0;
	el.style.transform = "translateY(30px)";
	el.style.transition = "all 0.6s ease-out";
	observer.observe(el);
});
