style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", system-ui, -apple-system, sans-serif;
}

.cta-section {
	padding: 100px 20px;
	background: linear-gradient(45deg, #f8f9fa 0%, #f1f3f5 100%);
}

.cta-container {
	max-width: 1200px;
	margin: 0 auto;
	display: grid;
	grid-template-columns: 1.2fr 1fr;
	gap: 60px;
	align-items: center;
}

.cta-content {
	position: relative;
	z-index: 1;
}

.cta-badge {
	display: inline-block;
	background: #4dabf7;
	color: white;
	padding: 8px 20px;
	border-radius: 50px;
	font-size: 0.9rem;
	margin-bottom: 20px;
}

.cta-title {
	font-size: 2.8rem;
	color: #212529;
	margin-bottom: 25px;
	line-height: 1.2;
}

.cta-text {
	color: #495057;
	font-size: 1.1rem;
	line-height: 1.7;
	margin-bottom: 35px;
}

.cta-form {
	display: flex;
	gap: 15px;
	max-width: 500px;
}

.cta-input {
	flex: 1;
	padding: 15px 25px;
	border: 2px solid #dee2e6;
	border-radius: 12px;
	font-size: 1rem;
	transition: all 0.3s ease;
}

.cta-input:focus {
	outline: none;
	border-color: #4dabf7;
	box-shadow: 0 0 0 3px rgba(77, 171, 247, 0.2);
}

.cta-submit {
	padding: 15px 35px;
	background: #4dabf7;
	color: white;
	border: none;
	border-radius: 12px;
	font-weight: 600;
	cursor: pointer;
	transition: all 0.3s ease;
}

.cta-submit:hover {
	background: #339af0;
	transform: translateY(-2px);
}

.cta-image-wrapper {
	position: relative;
	perspective: 1000px;
}

.cta-image {
	width: 100%;
	height: 500px;
	border-radius: 20px;
	overflow: hidden;
	transform: rotateY(-5deg);
	box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.15);
	transition: all 0.5s ease;
}

.cta-image:hover {
	transform: rotateY(0deg);
}

.cta-image img {
	width: 100%;
	height: 100%;
	object-fit: cover;
}

.trusted-by {
	margin-top: 40px;
	display: flex;
	align-items: center;
	gap: 20px;
	flex-wrap: wrap;
}

.trusted-text {
	color: #868e96;
	font-size: 0.9rem;
}

.trusted-logos {
	display: flex;
	gap: 25px;
	align-items: center;
}

.trusted-logo {
	height: 30px;
	opacity: 0.7;
	transition: opacity 0.3s ease;
}

.trusted-logo:hover {
	opacity: 1;
}

@media (max-width: 1024px) {
	.cta-container {
		grid-template-columns: 1fr;
		gap: 40px;
	}

	.cta-image {
		height: 400px;
	}

	.cta-title {
		font-size: 2.4rem;
	}
}

@media (max-width: 768px) {
	.cta-section {
		padding: 60px 20px;
	}

	.cta-title {
		font-size: 2rem;
	}

	.cta-form {
		flex-direction: column;
	}

	.cta-submit {
		width: 100%;
	}

	.trusted-by {
		flex-direction: column;
		align-items: flex-start;
	}
}

@keyframes float {
	0%,
	100% {
		transform: translateY(0);
	}
	50% {
		transform: translateY(-20px);
	}
}

script:
// Random image loader with different aspect ratio

document.addEventListener("DOMContentLoaded", () => {
	const ctaImage = document.getElementById("ctaImage");
	const randomSeed = Math.floor(Math.random() * 1000);
	ctaImage.src = `https://picsum.photos/seed/${randomSeed}/600/500`;
});

// Form validation and submission
const form = document.querySelector(".cta-form");
form.addEventListener("submit", (e) => {
	e.preventDefault();
	const email = form.querySelector("input").value;
	if (validateEmail(email)) {
		form.classList.add("submitted");
		setTimeout(() => {
			alert("Thank you for signing up! We'll be in touch.");
			form.reset();
		}, 500);
	}
});

function validateEmail(email) {
	const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
	return re.test(email);
}

// Add hover effect to trusted logos
document.querySelectorAll(".trusted-logo").forEach((logo) => {
	logo.addEventListener("mouseenter", () => {
		logo.style.transform = "scale(1.1)";
	});
	logo.addEventListener("mouseleave", () => {
		logo.style.transform = "scale(1)";
	});
});

// Intersection Observer for animation
const observer = new IntersectionObserver((entries) => {
	entries.forEach((entry) => {
		if (entry.isIntersecting) {
			entry.target.style.opacity = 1;
			entry.target.style.transform = "translateY(0)";
		}
	});
});

document.querySelectorAll(".cta-content, .cta-image-wrapper").forEach((el) => {
	el.style.opacity = 0;
	el.style.transform = "translateY(30px)";
	el.style.transition = "all 0.6s ease";
	observer.observe(el);
});
