style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", sans-serif;
}

.cta-section {
	padding: 100px 20px;
	background: linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.7)),
		url("https://picsum.photos/1920/1080?car") center/cover;
	color: white;
	position: relative;
}

.cta-container {
	max-width: 1200px;
	margin: 0 auto;
	display: flex;
	gap: 50px;
	align-items: center;
}

.cta-content {
	flex: 1;
	min-width: 300px;
}

.cta-form {
	flex: 1;
	background: rgba(255, 255, 255, 0.95);
	padding: 40px;
	border-radius: 15px;
	box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
}

.cta-title {
	font-size: 2.8rem;
	margin-bottom: 20px;
	line-height: 1.1;
}

.cta-text {
	font-size: 1.1rem;
	margin-bottom: 30px;
	line-height: 1.6;
	opacity: 0.9;
}

.form-group {
	margin-bottom: 20px;
}

.form-label {
	display: block;
	color: #2d3436;
	margin-bottom: 8px;
	font-weight: 600;
}

.form-input {
	width: 100%;
	padding: 14px;
	border: 2px solid #e0e0e0;
	border-radius: 8px;
	font-size: 1rem;
	transition: border-color 0.3s ease;
}

.form-input:focus {
	outline: none;
	border-color: #0984e3;
}

.cta-button {
	width: 100%;
	padding: 16px;
	background: #0984e3;
	color: white;
	border: none;
	border-radius: 8px;
	font-size: 1.1rem;
	font-weight: 600;
	cursor: pointer;
	transition: all 0.3s ease;
}

.cta-button:hover {
	background: #0873c4;
	transform: translateY(-2px);
}

.benefits-list {
	display: flex;
	gap: 30px;
	margin-top: 30px;
}

.benefit-item {
	display: flex;
	align-items: center;
	gap: 10px;
}

.benefit-icon {
	width: 24px;
	height: 24px;
	color: #0984e3;
}

@media (max-width: 1024px) {
	.cta-container {
		flex-direction: column;
		text-align: center;
	}

	.cta-form {
		width: 100%;
		max-width: 600px;
	}

	.benefits-list {
		flex-direction: column;
		gap: 20px;
	}
}

@media (max-width: 768px) {
	.cta-title {
		font-size: 2rem;
	}

	.cta-text {
		font-size: 1rem;
	}
}

script:
// Form validation
const form = document.querySelector(".cta-form");
form.addEventListener("submit", (e) => {
	e.preventDefault();

	const pickupLocation = document.getElementById("pickup-location").value;
	const pickupDate = document.getElementById("pickup-date").value;
	const returnDate = document.getElementById("return-date").value;

	if (!pickupLocation || !pickupDate || !returnDate) {
		alert("Please fill in all fields");
		return;
	}

	// Submit form logic
	console.log("Form submitted:", { pickupLocation, pickupDate, returnDate });
	alert("Searching for available cars...");
});

// Date input restrictions
const today = new Date().toISOString().split("T")[0];
document.getElementById("pickup-date").min = today;
document.getElementById("return-date").min = today;

// Update return date min when pickup date changes
document.getElementById("pickup-date").addEventListener("change", function () {
	document.getElementById("return-date").min = this.value;
});
