style:
:root {
	--primary: #e63946;
	--secondary: #f4a261;
	--text: #2d3439;
	--bg: #fff9f4;
	--card-bg: #ffffff;
	--transition: all 0.3s ease-in-out;
}

[data-theme="dark"] {
	--primary: #ff686b;
	--secondary: #ff9f1c;
	--text: #f8f9fa;
	--bg: #2d3439;
	--card-bg: #404040;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Arial", sans-serif;
}

body {
	background: var(--bg);
	color: var(--text);
	transition: var(--transition);
	line-height: 1.6;
}

.container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 3rem 2rem;
}

.theme-toggle {
	position: fixed;
	top: 1.5rem;
	right: 1.5rem;
	background: var(--card-bg);
	width: 50px;
	height: 50px;
	border-radius: 50%;
	border: none;
	cursor: pointer;
	box-shadow: 0 3px 6px rgba(0, 0, 0, 0.1);
	display: grid;
	place-items: center;
}

.header {
	text-align: center;
	margin-bottom: 4rem;
}

.title {
	font-size: 2.8rem;
	color: var(--primary);
	margin-bottom: 1rem;
}

.subtitle {
	max-width: 600px;
	margin: 0 auto;
	opacity: 0.9;
}

.features-grid {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
	gap: 2.5rem;
}

.feature-card {
	background: var(--card-bg);
	padding: 2.5rem;
	border-radius: 15px;
	text-align: center;
	transition: var(--transition);
	border: 1px solid rgba(0, 0, 0, 0.08);
}

.feature-card:hover {
	transform: translateY(-5px);
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
}

.feature-icon {
	width: 80px;
	height: 80px;
	background: var(--primary);
	border-radius: 50%;
	display: grid;
	place-items: center;
	margin: 0 auto 1.5rem;
	font-size: 2rem;
	color: white;
}

.feature-title {
	font-size: 1.4rem;
	margin-bottom: 1rem;
	color: var(--secondary);
}

.feature-text {
	font-size: 0.95rem;
	opacity: 0.9;
	line-height: 1.6;
}

@media (max-width: 768px) {
	.container {
		padding: 2rem 1rem;
	}

	.title {
		font-size: 2.2rem;
	}

	.feature-card {
		padding: 2rem;
	}
}

script:
const themeButton = document.querySelector(".theme-toggle");
const body = document.body;

// Theme management
const currentTheme = localStorage.getItem("theme") || "light";
body.setAttribute("data-theme", currentTheme);
updateThemeButton(currentTheme);

themeButton.addEventListener("click", () => {
	const newTheme = body.getAttribute("data-theme") === "dark" ? "light" : "dark";
	body.setAttribute("data-theme", newTheme);
	localStorage.setItem("theme", newTheme);
	updateThemeButton(newTheme);
});

function updateThemeButton(theme) {
	themeButton.innerHTML =
		theme === "dark" ? '<i class="fas fa-moon"></i>' : '<i class="fas fa-pizza-slice"></i>';
}
