style:
body {
	font-family: "Permanent Marker", cursive;
	background: #f0f0f0;
	color: #333;
	margin: 0;
	padding: 0;
	transition: background-color 0.3s, color 0.3s;
}
h1 {
	text-align: center;
	color: #0077b6;
	padding: 40px 0;
	font-size: 2.5em;
	text-shadow: 2px 2px 0 #000;
	border-top: 5px double #0077b6;
	border-bottom: 5px double #0077b6;
}
.container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 20px;
}
.features {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
	gap: 30px;
}
.feature {
	background: #fff;
	border: 4px solid #000;
	border-radius: 15px;
	padding: 20px;
	position: relative;
	transition: all 0.3s ease;
	overflow: hidden;
	box-shadow: 5px 5px 0 rgba(0, 0, 0, 0.3);
}
.feature::after {
	content: "";
	position: absolute;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
	border-radius: 15px;
	border: 2px dashed #0077b6;
	z-index: -1;
	opacity: 0;
	transition: opacity 0.3s ease;
}
.feature:hover::after {
	opacity: 1;
}
.feature:hover {
	transform: translateY(-5px);
}
.feature i {
	font-size: 2.5em;
	color: #0077b6;
	margin-bottom: 15px;
	transition: all 0.3s ease;
	text-shadow: 1px 1px 0 #000;
}
.feature:hover i {
	transform: scale(1.2);
}
.feature h3 {
	margin: 10px 0 5px;
	color: #0077b6;
	font-weight: bold;
	text-shadow: 1px 1px 0 #000;
}
.feature p {
	font-style: normal;
	font-size: 1em;
	line-height: 1.5;
	position: relative;
	padding: 10px;
	background: #fff;
	border: 2px solid #000;
	border-radius: 10px;
}
.feature p::before,
.feature p::after {
	content: "";
	position: absolute;
	width: 0;
	height: 0;
	border-style: solid;
}
.feature p::before {
	bottom: 100%;
	left: 10px;
	border-width: 0 10px 10px 0;
	border-color: transparent #000 transparent transparent;
}
.feature p::after {
	bottom: 100%;
	left: 12px;
	border-width: 0 8px 8px 0;
	border-color: transparent #fff transparent transparent;
}
.theme-toggle {
	position: fixed;
	top: 20px;
	right: 20px;
	background: #0077b6;
	border: 3px solid #000;
	color: #fff;
	padding: 10px;
	border-radius: 50%;
	font-size: 24px;
	cursor: pointer;
	transition: transform 0.3s ease;
}
.theme-toggle:hover {
	transform: scale(1.1);
}
.dark-mode {
	background: #333;
	color: #f0f0f0;
}
.dark-mode .feature {
	background: #4a4a4a;
	border-color: #fff;
}
.dark-mode .feature i {
	color: #4dabf7;
}
.dark-mode .feature::after {
	border-color: #4dabf7;
}
.dark-mode .feature h3 {
	color: #4dabf7;
}
.dark-mode .feature p {
	background: #4a4a4a;
	border-color: #fff;
}
.dark-mode .feature p::after {
	border-color: transparent #4a4a4a transparent transparent;
}

script:
function toggleTheme() {
	document.body.classList.toggle("dark-mode");
	const icon = document.getElementById("theme-icon");
	if (icon.classList.contains("fa-sun")) {
		icon.classList.remove("fa-sun");
		icon.classList.add("fa-moon");
	} else {
		icon.classList.remove("fa-moon");
		icon.classList.add("fa-sun");
	}
}
