style:
:root {
	--primary: #3b82f6;
	--secondary: #2563eb;
	--bg: #ffffff;
	--text: #1e293b;
	--card-bg: #f8fafc;
	--highlight: #f59e0b;
}

.dark-theme {
	--primary: #60a5fa;
	--secondary: #3b82f6;
	--bg: #0f172a;
	--text: #f8fafc;
	--card-bg: #1e293b;
	--highlight: #fbbf24;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: all 0.3s ease;
}

body {
	font-family: "Inter", sans-serif;
	background-color: var(--bg);
	color: var(--text);
	line-height: 1.5;
}

.container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 4rem 1rem;
}

.theme-toggle {
	position: fixed;
	top: 1.5rem;
	right: 1.5rem;
	background: var(--card-bg);
	border: 2px solid var(--primary);
	padding: 0.8rem;
	border-radius: 50%;
	cursor: pointer;
	color: var(--primary);
}

.features {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
	gap: 2rem;
	margin-top: 2rem;
}

.feature-card {
	background: var(--card-bg);
	padding: 2rem;
	border-radius: 1rem;
	border: 1px solid #e2e8f0;
	position: relative;
	overflow: hidden;
}

.feature-icon {
	font-size: 2.5rem;
	color: var(--primary);
	margin-bottom: 1.5rem;
}

.feature-card h3 {
	margin-bottom: 1rem;
	color: var(--text);
}

.feature-card p {
	color: var(--text);
	opacity: 0.9;
	margin-bottom: 1.5rem;
}

.badge {
	background: var(--primary);
	color: white;
	padding: 0.25rem 0.75rem;
	border-radius: 1rem;
	font-size: 0.8rem;
	display: inline-block;
	position: absolute;
	top: 1rem;
	right: 1rem;
}

.price {
	color: var(--highlight);
	font-size: 1.25rem;
	font-weight: bold;
	margin-top: 1rem;
}

.feature-card:hover {
	transform: translateY(-5px);
	box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
}

@media (max-width: 768px) {
	.features {
		grid-template-columns: 1fr;
	}
}

script:
function toggleTheme() {
	document.body.classList.toggle("dark-theme");
	const themeToggle = document.querySelector(".theme-toggle");
	const isDark = document.body.classList.contains("dark-theme");

	themeToggle.innerHTML = isDark ? '<i class="fas fa-sun"></i>' : '<i class="fas fa-moon"></i>';

	localStorage.setItem("theme", isDark ? "dark" : "light");
}

// Load saved theme
const savedTheme = localStorage.getItem("theme");
if (savedTheme === "dark") {
	document.body.classList.add("dark-theme");
	document.querySelector(".theme-toggle").innerHTML = '<i class="fas fa-sun"></i>';
}
