style:
body {
	font-family: "Roboto", sans-serif;
	background: #e8f6f3; /* Soft green for a nature vibe */
	color: #333;
	margin: 0;
	padding: 0;
	transition: background-color 0.3s, color 0.3s;
}

h1 {
	text-align: center;
	color: #1e8449; /* Dark green for emphasis */
	padding: 40px 0;
	font-weight: 300;
}

.container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 20px;
}

.features {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
	gap: 30px;
}

.feature {
	background: #fff;
	border-radius: 50% 50% 0 0; /*Top rounded corners for a unique look*/
	padding: 30px 20px 20px; /*More padding at the top for icon placement*/
	position: relative;
	overflow: hidden;
	box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
	transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.feature::before {
	content: "";
	position: absolute;
	bottom: 0;
	left: 0;
	width: 100%;
	height: 10px;
	background: #1e8449;
}

.feature:hover {
	transform: translateY(-10px);
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.15);
}

.feature i {
	position: absolute;
	top: -10px; /*Partially outside for a unique look*/
	left: 50%;
	transform: translateX(-50%);
	font-size: 4em;
	color: #1e8449;
	z-index: -1;
	opacity: 0.3;
	transition: all 0.3s ease;
}

.feature:hover i {
	transform: translateX(-50%) scale(1.1);
	opacity: 0.4;
}

.feature h3 {
	margin-top: 30px;
	color: #1e8449;
	font-weight: 500;
}

.theme-toggle {
	position: fixed;
	top: 20px;
	right: 20px;
	background: #1e8449;
	border: none;
	color: white;
	padding: 10px;
	border-radius: 50%;
	font-size: 24px;
	cursor: pointer;
	transition: transform 0.3s ease;
}

.theme-toggle:hover {
	transform: scale(1.1);
}

.dark-mode {
	background: #2c3e50; /* Dark blue-green for dark mode */
	color: #ecf0f1;
}

.dark-mode .feature {
	background: #34495e;
}

.dark-mode .feature i {
	color: #27ae60; /* Lighter green for icons in dark mode */
}

.dark-mode .feature::before {
	background: #27ae60;
}

.dark-mode .feature h3 {
	color: #ecf0f1;
}

script:
function toggleTheme() {
	document.body.classList.toggle("dark-mode");
	const icon = document.getElementById("theme-icon");
	if (icon.classList.contains("fa-sun")) {
		icon.classList.remove("fa-sun");
		icon.classList.add("fa-moon");
	} else {
		icon.classList.remove("fa-moon");
		icon.classList.add("fa-sun");
	}
}
