style:
body {
	font-family: "Helvetica Neue", sans-serif;
	background: #f9f9f9;
	color: #333;
	margin: 0;
	padding: 0;
	transition: background-color 0.3s, color 0.3s;
}

h1 {
	text-align: center;
	color: #ff4500;
	padding: 40px 0;
	font-weight: 200;
	letter-spacing: 2px;
}

.container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 20px;
}

.features {
	display: flex;
	flex-wrap: wrap;
	justify-content: space-between;
	gap: 20px;
}

.feature {
	background: #fff;
	border-radius: 0 25px 0 25px;
	padding: 20px;
	position: relative;
	overflow: hidden;
	width: calc(50% - 50px);
	min-width: 250px;
	transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.feature::before {
	content: "";
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	background: linear-gradient(45deg, #ff4500, transparent);
	opacity: 0;
	transition: opacity 0.3s ease;
	z-index: -1;
}

.feature:hover {
	transform: translateX(10px);
	box-shadow: 5px 5px 15px rgba(0, 0, 0, 0.1);
}

.feature:hover::before {
	opacity: 0.1;
}

.feature i {
	font-size: 2.5em;
	color: #ff4500;
	margin-bottom: 10px;
	transition: transform 0.3s ease;
}

.feature:hover i {
	transform: scale(1.1);
}

.feature h3 {
	margin: 10px 0 5px;
	color: #333;
	font-weight: 600;
}

.theme-toggle {
	position: fixed;
	top: 20px;
	right: 20px;
	background: #ff4500;
	border: none;
	color: white;
	padding: 10px;
	border-radius: 50%;
	font-size: 24px;
	cursor: pointer;
	transition: transform 0.3s ease;
}

.theme-toggle:hover {
	transform: scale(1.1);
}

.dark-mode {
	background: #333;
	color: #f9f9f9;
}

.dark-mode .feature {
	background: #4a4a4a;
}

.dark-mode .feature i {
	color: #ff8c00;
}

.dark-mode .feature::before {
	background: linear-gradient(45deg, #ff8c00, transparent);
}

.dark-mode .feature h3 {
	color: #f9f9f9;
}

@media screen and (max-width: 768px) {

	.feature {
		width: 100%
	}
}

script:
function toggleTheme() {
	document.body.classList.toggle("dark-mode");
	const icon = document.getElementById("theme-icon");
	if (icon.classList.contains("fa-sun")) {
		icon.classList.remove("fa-sun");
		icon.classList.add("fa-moon");
	} else {
		icon.classList.remove("fa-moon");
		icon.classList.add("fa-sun");
	}
}
