style:
body {
	font-family: "Orbitron", sans-serif;
	background: #1a1a1a;
	color: #ccc;
	margin: 0;
	padding: 0;
	transition: background-color 0.3s, color 0.3s;
}

h1 {
	text-align: center;
	color: #00ff41;
	padding: 40px 0;
	font-weight: 700;
	text-shadow: 0 0 10px #00ff41;
}

.container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 20px;
}

.features {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
	gap: 30px;
}

.feature {
	background: #2c2c2c;
	border: 1px solid #00ff41;
	border-radius: 10px;
	padding: 20px;
	position: relative;
	overflow: hidden;
	transition: all 0.3s ease;
}

.feature::before {
	content: "";
	position: absolute;
	top: -50%;
	left: -50%;
	width: 200%;
	height: 200%;
	background: radial-gradient(circle at center, rgba(0, 255, 65, 0.1), transparent);
	opacity: 0;
	transition: opacity 0.3s ease, transform 0.3s ease;
	z-index: 0;
}

.feature:hover {
	transform: scale(1.05);
	border-color: #00ff41;
}

.feature:hover::before {
	opacity: 1;
	transform: rotate(30deg);
}

.feature i {
	font-size: 2.5em;
	color: #00ff41;
	margin-bottom: 15px;
	transition: all 0.3s ease;
	text-shadow: 0 0 5px #00ff41;
}

.feature:hover i {
	transform: scale(1.1);
}

.feature h3 {
	margin: 10px 0 5px;
	color: #fff;
	font-weight: 600;
}

.theme-toggle {
	position: fixed;
	top: 20px;
	right: 20px;
	background: #00ff41;
	border: none;
	color: #1a1a1a;
	padding: 10px;
	border-radius: 50%;
	font-size: 24px;
	cursor: pointer;
	transition: transform 0.3s ease;
}

.theme-toggle:hover {
	transform: scale(1.1);
}

.dark-mode {
	background: #000;
	color: #ccc;
}

.dark-mode .feature {
	background: #1c1c1c;
	border-color: #00ff41;
}

.dark-mode .feature i {
	color: #00ff41;
}

.dark-mode .feature::before {
	background: radial-gradient(circle at center, rgba(0, 255, 65, 0.2), transparent);
}

.dark-mode .feature h3 {
	color: #fff;
}

script:
function toggleTheme() {
	document.body.classList.toggle("dark-mode");
	const icon = document.getElementById("theme-icon");
	if (icon.classList.contains("fa-sun")) {
		icon.classList.remove("fa-sun");
		icon.classList.add("fa-moon");
	} else {
		icon.classList.remove("fa-moon");
		icon.classList.add("fa-sun");
	}
}
