style:
:root {
  --primary: #4f46e5;
  --secondary: #6366f1;
  --bg: #ffffff;
  --text: #1f2937;
  --card-bg: #f8fafc;
  --accent: #f59e0b;
}

.dark-theme {
  --primary: #818cf8;
  --secondary: #a5b4fc;
  --bg: #111827;
  --text: #f3f4f6;
  --card-bg: #1f2937;
  --accent: #fbbf24;
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  transition: all 0.3s ease;
}

body {
  font-family: "Playfair Display", serif;
  background-color: var(--bg);
  color: var(--text);
  line-height: 1.6;
}

.container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 4rem 1rem;
}

.theme-toggle {
  position: fixed;
  top: 1.5rem;
  right: 1.5rem;
  background: var(--card-bg);
  border: 2px solid var(--primary);
  padding: 0.8rem;
  border-radius: 50%;
  cursor: pointer;
  color: var(--primary);
}

.features {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
  margin-top: 3rem;
}

.feature-card {
  background: var(--card-bg);
  padding: 2.5rem;
  border-radius: 1rem;
  position: relative;
  overflow: hidden;
}

.feature-card::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 4px;
  height: 100%;
  background: linear-gradient(var(--primary), var(--secondary));
}

.feature-icon {
  font-size: 2.2rem;
  color: var(--primary);
  margin-bottom: 1.5rem;
}

.feature-card h3 {
  font-size: 1.5rem;
  margin-bottom: 1rem;
  color: var(--text);
}

.feature-card p {
  color: var(--text);
  opacity: 0.9;
  margin-bottom: 1.5rem;
}

.service-list {
  list-style: none;
  margin-top: 1rem;
}

.service-list li {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin-bottom: 0.5rem;
  color: var(--text);
  opacity: 0.9;
}

.service-list li i {
  color: var(--accent);
  font-size: 0.8rem;
}

.feature-card:hover {
  transform: translateY(-5px);
  box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
}

@media (max-width: 768px) {
  .features {
    grid-template-columns: 1fr;
  }

  .feature-card {
    padding: 2rem;
  }
}

script:
function toggleTheme() {
  document.body.classList.toggle("dark-theme");
  const themeToggle = document.querySelector(".theme-toggle");
  const isDark = document.body.classList.contains("dark-theme");

  themeToggle.innerHTML = isDark ? '<i class="fas fa-sun"></i>' : '<i class="fas fa-moon"></i>';

  localStorage.setItem("theme", isDark ? "dark" : "light");
}

// Load saved theme
const savedTheme = localStorage.getItem("theme");
if (savedTheme === "dark") {
  document.body.classList.add("dark-theme");
  document.querySelector(".theme-toggle").innerHTML = '<i class="fas fa-sun"></i>';
}
