style:
body {
	font-family: "Cyberpunk", "Orbitron", sans-serif;
	background: #0a0a0a; /* Almost black for a cyberpunk night */
	color: #ddd;
	margin: 0;
	padding: 0;
	transition: background-color 0.3s, color 0.3s;
}
h1 {
	text-align: center;
	color: #ff4500; /* Neon orange */
	padding: 40px 0;
	font-weight: 900;
	text-shadow: 0 0 10px #ff4500, 0 0 20px #ff4500;
	animation: neon-blink 2s infinite;
}
@keyframes neon-blink {
	0%,
	100% {
		opacity: 1;
	}
	50% {
		opacity: 0.5;
	}
}
.container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 20px;
}
.features {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
	gap: 30px;
}
.feature {
	background: #1a1a1a;
	border-radius: 0 30px 0 30px; /* Asymmetric for cyberpunk edge */
	padding: 20px;
	position: relative;
	overflow: hidden;
	transition: transform 0.3s ease, box-shadow 0.3s ease, all 0.4s ease;
	border: 2px solid transparent;
}
.feature::before {
	content: "";
	position: absolute;
	top: 0;
	left: -100%;
	width: 200%;
	height: 100%;
	background: linear-gradient(90deg, transparent, rgba(255, 69, 0, 0.1), transparent);
	transform: skewX(-45deg);
	transition: left 0.5s ease;
	z-index: 0;
}
.feature:hover {
	transform: translateY(-5px) scale(1.02);
	box-shadow: 0 0 20px rgba(255, 69, 0, 0.5);
	border-radius: 30px 0 30px 0; /* Asymmetric for cyberpunk edge */
}
.feature:hover::before {
	left: 100%;
}
.feature i {
	font-size: 3em;
	color: #ff4500;
	margin-bottom: 15px;
	transition: all 0.3s ease;
	text-shadow: 0 0 10px #ff4500;
}
.feature:hover i {
	transform: scale(1.1);
}
.feature h3 {
	margin: 10px 0 5px;
	color: #ff4500;
	font-weight: 600;
	text-shadow: 0 0 5px #ff4500;
}
.theme-toggle {
	position: fixed;
	top: 20px;
	right: 20px;
	background: transparent;
	border: 2px solid #ff4500;
	color: #ff4500;
	padding: 10px;
	border-radius: 50%;
	font-size: 24px;
	cursor: pointer;
	transition: all 0.3s ease;
	animation: neon-blink 2s infinite;
}
.theme-toggle:hover {
	transform: scale(1.1);
	background: #ff4500;
	color: #0a0a0a;
}
.dark-mode {
	background: #000; /* Pure black for true cyberpunk dark mode */
	color: #ddd;
}
.dark-mode .feature {
	background: #111;
	border-color: #ff8c00; /* Darker orange for contrast */
}
.dark-mode .feature i {
	color: #ff8c00;
}
.dark-mode .feature::before {
	background: linear-gradient(90deg, transparent, rgba(255, 140, 0, 0.1), transparent);
}
.dark-mode .feature h3 {
	color: #ff8c00;
}

script:
function toggleTheme() {
	document.body.classList.toggle("dark-mode");
	const icon = document.getElementById("theme-icon");
	if (icon.classList.contains("fa-sun")) {
		icon.classList.remove("fa-sun");
		icon.classList.add("fa-moon");
	} else {
		icon.classList.remove("fa-moon");
		icon.classList.add("fa-sun");
	}
}
