style:
body {
	font-family: "Bangers", "Comic Sans MS", cursive;
	background: #e6e6e6; /* Light grey for paper comic background */
	color: #333;
	margin: 0;
	padding: 0;
	transition: background-color 0.3s, color 0.3s;
}

h1 {
	text-align: center;
	color: #ff1493; /* Bright pink for comic pop */
	padding: 40px 0;
	font-size: 3em;
	text-shadow: 2px 2px 0 #000;
	border-bottom: 5px dashed #ff1493;
}

.container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 20px;
}

.features {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
	gap: 30px;
}

.feature {
	background: #fff;
	border: 3px solid #000; /* Comic book panel border */
	border-radius: 10px;
	padding: 20px;
	position: relative;
	transition: all 0.3s ease;
	overflow: hidden;
}

.feature::before {
	content: "";
	position: absolute;
	top: -10px;
	left: -10px;
	right: -10px;
	bottom: -10px;
	border: 2px dotted #ff1493;
	z-index: -1;
	opacity: 0;
	transition: opacity 0.3s ease;
}

.feature:hover::before {
	opacity: 1;
}

.feature:hover {
	transform: scale(1.05);
	box-shadow: 5px 5px 0 rgba(0, 0, 0, 0.3);
}

.feature i {
	font-size: 3em;
	color: #ff1493;
	margin-bottom: 15px;
	transition: all 0.3s ease;
	text-shadow: 1px 1px 0 #000;
}

.feature:hover i {
	transform: scale(1.1);
}

.feature h3 {
	margin: 10px 0 5px;
	color: #ff1493;
	font-weight: bold;
	text-shadow: 1px 1px 0 #000;
}

.feature p {
	font-style: italic;
	font-size: 0.9em;
}

/* Speech bubble for descriptions */
.feature p::before {
	content: open-quote;
}

.feature p::after {
	content: close-quote;
}

.theme-toggle {
	position: fixed;
	top: 20px;
	right: 20px;
	background: #ff1493;
	border: 2px solid #000;
	color: #fff;
	padding: 10px;
	border-radius: 50%;
	font-size: 24px;
	cursor: pointer;
	transition: transform 0.3s ease;
}

.theme-toggle:hover {
	transform: scale(1.1);
}

.dark-mode {
	background: #333;
	color: #e6e6e6;
}

.dark-mode .feature {
	background: #4a4a4a;
	border-color: #fff;
}

.dark-mode .feature i {
	color: #ff69b4; /* Lighter pink for dark mode */
}

.dark-mode .feature::before {
	border-color: #ff69b4;
}

.dark-mode .feature h3 {
	color: #ff69b4;
}

script:
function toggleTheme() {
	document.body.classList.toggle("dark-mode");
	const icon = document.getElementById("theme-icon");
	if (icon.classList.contains("fa-sun")) {
		icon.classList.remove("fa-sun");
		icon.classList.add("fa-moon");
	} else {
		icon.classList.remove("fa-moon");
		icon.classList.add("fa-sun");
	}
}
