style:
:root {
	--primary: #6366f1;
	--secondary: #8b5cf6;
	--bg: #f1f5f9;
	--text: #1e293b;
	--card-bg: #ffffff;
	--icon-bg: #e0e7ff;
}

.dark-theme {
	--primary: #818cf8;
	--secondary: #a78bfa;
	--bg: #0f172a;
	--text: #f8fafc;
	--card-bg: #1e293b;
	--icon-bg: #2e3750;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: all 0.3s ease;
}

body {
	font-family: system-ui, -apple-system, sans-serif;
	background: var(--bg);
	color: var(--text);
	min-height: 100vh;
}

.container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 4rem 1rem;
}

.theme-toggle {
	position: fixed;
	top: 1.5rem;
	right: 1.5rem;
	background: var(--card-bg);
	border: none;
	width: 40px;
	height: 40px;
	border-radius: 50%;
	cursor: pointer;
	display: grid;
	place-items: center;
	box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
}

.features {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
	gap: 2rem;
	position: relative;
}

.feature-card {
	background: var(--card-bg);
	padding: 2.5rem 2rem;
	border-radius: 1.5rem;
	position: relative;
	overflow: hidden;
}

.feature-card::before {
	content: "";
	position: absolute;
	top: -50px;
	right: -50px;
	width: 120px;
	height: 120px;
	background: linear-gradient(45deg, var(--primary), var(--secondary));
	border-radius: 50%;
	opacity: 0.1;
	transition: .4s ease;
}

.feature-card:hover::before {
	width: 500%;
	height: 500%;
	border-radius: 0;
}

.feature-icon {
	width: 60px;
	height: 60px;
	background: var(--icon-bg);
	border-radius: 1rem;
	display: grid;
	place-items: center;
	font-size: 1.8rem;
	color: var(--primary);
	margin-bottom: 2rem;
}

.feature-card h3 {
	font-size: 1.3rem;
	margin-bottom: 1rem;
	color: var(--text);
}

.feature-card p {
	color: var(--text);
	opacity: 0.9;
	line-height: 1.6;
	font-size: 0.95rem;
}

.feature-card:hover {
	transform: translateY(-8px);
}

@media (max-width: 768px) {
	.container {
		padding: 3rem 1rem;
	}

	.feature-card {
		padding: 2rem 1.5rem;
	}
}

script:
function toggleTheme() {
	document.body.classList.toggle("dark-theme");
	const themeToggle = document.querySelector(".theme-toggle");
	const isDark = document.body.classList.contains("dark-theme");

	themeToggle.innerHTML = isDark ? '<i class="fas fa-sun"></i>' : '<i class="fas fa-moon"></i>';

	localStorage.setItem("theme", isDark ? "dark" : "light");
}

// Load saved theme
const savedTheme = localStorage.getItem("theme");
if (savedTheme === "dark") {
	document.body.classList.add("dark-theme");
	document.querySelector(".theme-toggle").innerHTML = '<i class="fas fa-sun"></i>';
}
