style:
:root {
	--primary: #f43f5e;
	--secondary: #f97316;
	--bg: #ffffff;
	--text: #1f2937;
	--card-bg: #fff7ed;
	--stats-bg: #ffe4e6;
}

.dark-theme {
	--primary: #fb7185;
	--secondary: #fdba74;
	--bg: #0f172a;
	--text: #f8fafc;
	--card-bg: #1e293b;
	--stats-bg: #2e1f2e;
}

*
{
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: all 0.3s ease;
}

body {
	font-family: "Arial", sans-serif;
	background: linear-gradient(45deg, var(--bg), #f3f4f6);
	color: var(--text);
	line-height: 1.6;
}

.container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 4rem 1rem;
}

.theme-toggle {
	position: fixed;
	bottom: 2rem;
	right: 2rem;
	background: var(--primary);
	color: white;
	border: none;
	padding: 1rem;
	border-radius: 50%;
	cursor: pointer;
	box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
}

.features {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
	gap: 2rem;
}

.feature-card {
	background: var(--card-bg);
	padding: 2.5rem;
	border-radius: 1.5rem;
	position: relative;
	overflow: hidden;
	text-align: center;
}

.feature-icon {
	font-size: 2.5rem;
	color: var(--primary);
	margin-bottom: 1.5rem;
	position: relative;
	z-index: 1;
}

.feature-card h3 {
	color: var(--text);
	margin-bottom: 1rem;
	font-size: 1.5rem;
}

.feature-card p {
	color: var(--text);
	opacity: 0.9;
	margin-bottom: 1.5rem;
}

.stats-badge {
	background: var(--stats-bg);
	color: var(--primary);
	padding: 0.5rem 1rem;
	border-radius: 2rem;
	font-weight: bold;
	font-size: 0.9rem;
	display: inline-block;
}

.feature-card:hover {
	transform: translateY(-5px);
	box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
}

.feature-card::after {
	content: "";
	position: absolute;
	bottom: 0;
	left: 0;
	width: 100%;
	height: 4px;
	background: linear-gradient(90deg, var(--primary), var(--secondary));
}

@media (max-width: 768px) {
	.features {
		grid-template-columns: 1fr;
	}

	.feature-card {
		padding: 2rem;
	}
}

script:
function toggleTheme() {
	document.body.classList.toggle("dark-theme");
	const themeToggle = document.querySelector(".theme-toggle");
	const isDark = document.body.classList.contains("dark-theme");

	themeToggle.innerHTML = isDark ? '<i class="fas fa-sun"></i>' : '<i class="fas fa-moon"></i>';

	localStorage.setItem("theme", isDark ? "dark" : "light");
}

// Load saved theme
const savedTheme = localStorage.getItem("theme");
if (savedTheme === "dark") {
	document.body.classList.add("dark-theme");
	document.querySelector(".theme-toggle").innerHTML = '<i class="fas fa-sun"></i>';
}
