style:
:root {
	--primary: #2d6a4f;
	--secondary: #40916c;
	--text: #1b4332;
	--bg: #f8f9fa;
	--card-bg: #ffffff;
	--transition: all 0.3s ease;
}

[data-theme="dark"] {
	--primary: #74c69d;
	--secondary: #b7e4c7;
	--text: #d8f3dc;
	--bg: #1b4332;
	--card-bg: #2d6a4f;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Arial", sans-serif;
}

body {
	background: var(--bg);
	color: var(--text);
	transition: var(--transition);
	line-height: 1.6;
}

.container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 2rem;
}

.theme-toggle {
	position: fixed;
	top: 1rem;
	right: 1rem;
	background: var(--card-bg);
	border: none;
	padding: 0.8rem;
	border-radius: 50%;
	cursor: pointer;
	box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
}

.section-header {
	text-align: center;
	margin-bottom: 3rem;
}

.section-title {
	font-size: 2.5rem;
	color: var(--primary);
	margin-bottom: 1rem;
}

.section-subtitle {
	color: var(--text);
	opacity: 0.9;
	max-width: 600px;
	margin: 0 auto;
}

.features-grid {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
	gap: 2rem;
}

.feature-card {
	background: var(--card-bg);
	padding: 2rem;
	border-radius: 15px;
	text-align: center;
	transition: var(--transition);
	border: 1px solid rgba(0, 0, 0, 0.05);
}

.feature-card:hover {
	transform: translateY(-5px);
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
}

.nature-icon {
	font-size: 2.5rem;
	color: var(--primary);
	margin-bottom: 1.5rem;
	transition: var(--transition);
}

.feature-title {
	font-size: 1.3rem;
	margin-bottom: 1rem;
	color: var(--secondary);
}

.feature-text {
	font-size: 0.95rem;
	opacity: 0.9;
	line-height: 1.5;
}

@media (max-width: 768px) {
	.container {
		padding: 1rem;
	}

	.section-title {
		font-size: 2rem;
	}
}

script:
const themeToggle = document.querySelector(".theme-toggle");
const body = document.body;

// Theme management
const savedTheme = localStorage.getItem("theme") || "light";
body.setAttribute("data-theme", savedTheme);
updateThemeIcon(savedTheme);

themeToggle.addEventListener("click", () => {
	const currentTheme = body.getAttribute("data-theme");
	const newTheme = currentTheme === "dark" ? "light" : "dark";
	body.setAttribute("data-theme", newTheme);
	localStorage.setItem("theme", newTheme);
	updateThemeIcon(newTheme);
});

function updateThemeIcon(theme) {
	themeToggle.innerHTML = theme === "dark" ? '<i class="fas fa-sun"></i>' : '<i class="fas fa-leaf"></i>';
}
