style:
:root {
	--primary: #2a9d8f;
	--secondary: #264653;
	--text: #2d3439;
	--bg: #f1faee;
	--card-bg: #ffffff;
	--transition: all 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94);
}

[data-theme="dark"] {
	--primary: #83c5be;
	--secondary: #a8dadc;
	--text: #edf6f9;
	--bg: #1d3557;
	--card-bg: #2a9d8f;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Segoe UI", system-ui, sans-serif;
}

body {
	background: var(--bg);
	color: var(--text);
	transition: var(--transition);
	line-height: 1.5;
}

.container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 3rem 2rem;
}

.theme-switch {
	position: fixed;
	top: 1.5rem;
	right: 1.5rem;
	background: var(--card-bg);
	width: 45px;
	height: 45px;
	border-radius: 50%;
	border: none;
	cursor: pointer;
	box-shadow: 0 3px 6px rgba(0, 0, 0, 0.1);
	display: grid;
	place-items: center;
}

.section-heading {
	text-align: center;
	margin-bottom: 4rem;
}

.section-title {
	font-size: 2.8rem;
	color: var(--primary);
	margin-bottom: 1rem;
}

.section-description {
	max-width: 600px;
	margin: 0 auto;
	opacity: 0.9;
}

.eco-grid {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
	gap: 2.5rem;
}

.eco-card {
	background: var(--card-bg);
	padding: 2.5rem;
	border-radius: 20px;
	text-align: center;
	transition: var(--transition);
	border: 1px solid rgba(0, 0, 0, 0.08);
}

.eco-card:hover {
	transform: translateY(-8px);
	box-shadow: 0 12px 24px rgba(0, 0, 0, 0.08);
}

.eco-icon {
	width: 70px;
	height: 70px;
	background: var(--primary);
	border-radius: 50%;
	display: grid;
	place-items: center;
	margin: 0 auto 1.5rem;
	font-size: 1.8rem;
	color: white;
}

.eco-title {
	font-size: 1.4rem;
	margin-bottom: 1rem;
	color: var(--secondary);
}

.eco-text {
	font-size: 0.95rem;
	opacity: 0.9;
	line-height: 1.6;
}

@media (max-width: 768px) {
	.container {
		padding: 2rem 1rem;
	}

	.section-title {
		font-size: 2.2rem;
	}

	.eco-card {
		padding: 2rem;
	}
}

script:
const themeButton = document.querySelector(".theme-switch");
const body = document.body;

// Theme management
const currentTheme = localStorage.getItem("theme") || "light";
body.setAttribute("data-theme", currentTheme);
updateThemeButton(currentTheme);

themeButton.addEventListener("click", () => {
	const newTheme = body.getAttribute("data-theme") === "dark" ? "light" : "dark";
	body.setAttribute("data-theme", newTheme);
	localStorage.setItem("theme", newTheme);
	updateThemeButton(newTheme);
});

function updateThemeButton(theme) {
	themeButton.innerHTML =
		theme === "dark" ? '<i class="fas fa-sun"></i>' : '<i class="fas fa-mountain"></i>';
}
