style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
}

.footer {
	background-color: #2d3436;
	color: #ffffff;
	padding: 50px 0 20px;
	margin-top: 50px;
}

.footer-content {
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 20px;
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
	gap: 30px;
}

.footer-section {
	margin-bottom: 30px;
}

.footer-section h3 {
	color: #0984e3;
	margin-bottom: 20px;
	font-size: 1.4rem;
}

.footer-section p {
	font-size: 0.9rem;
	line-height: 1.6;
	opacity: 0.9;
}

.footer-links {
	list-style: none;
}

.footer-links li {
	margin-bottom: 10px;
}

.footer-links a {
	color: #ffffff;
	text-decoration: none;
	opacity: 0.8;
	transition: opacity 0.3s ease;
}

.footer-links a:hover {
	opacity: 1;
	color: #0984e3;
}

.contact-list {
	list-style: none;
}

.contact-list li {
	margin-bottom: 15px;
	display: flex;
	align-items: center;
}

.contact-list i {
	margin-right: 10px;
	color: #0984e3;
}

.footer-logo img {
	width: 150px;
	margin-bottom: 20px;
}

.social-links {
	display: flex;
	gap: 15px;
	margin-top: 20px;
}

.social-links a {
	color: #ffffff;
	font-size: 1.5rem;
	opacity: 0.8;
	transition: opacity 0.3s ease;
}

.social-links a:hover {
	opacity: 1;
	color: #0984e3;
}

.footer-bottom {
	text-align: center;
	padding-top: 50px;
	border-top: 1px solid rgba(255, 255, 255, 0.1);
	margin-top: 30px;
}

.footer-bottom p {
	font-size: 0.9rem;
	opacity: 0.8;
}

@media (max-width: 768px) {
	.footer-content {
		grid-template-columns: 1fr;
	}

	.footer-section {
		text-align: center;
	}

	.social-links {
		justify-content: center;
	}

	.contact-list li {
		justify-content: center;
	}
}

/* Back to top button */

.back-to-top {
	position: fixed;
	bottom: 30px;
	right: 30px;
	background-color: #0984e3;
	color: white;
	width: 40px;
	height: 40px;
	border-radius: 50%;
	display: none;
	align-items: center;
	justify-content: center;
	cursor: pointer;
	transition: background-color 0.3s;
}

.back-to-top:hover {
	background-color: #0a6ebd;
}

script:
// Back to top button
const backToTop = document.getElementById("backToTop");

window.addEventListener("scroll", () => {
	if (window.pageYOffset > 300) {
		backToTop.style.display = "flex";
	} else {
		backToTop.style.display = "none";
	}
});

backToTop.addEventListener("click", () => {
	window.scrollTo({
		top: 0,
		behavior: "smooth",
	});
});

// Add smooth scrolling to all links
document.querySelectorAll('a[href^="#"]').forEach((anchor) => {
	anchor.addEventListener("click", function (e) {
		e.preventDefault();
		document.querySelector(this.getAttribute("href")).scrollIntoView({
			behavior: "smooth",
		});
	});
});
