style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", sans-serif;
}

.footer {
	background: #f8f9fa;
	color: #2d3436;
	padding: 4rem 0;
	border-top: 1px solid #e0e0e0;
}

.footer-container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 2rem;
}

.footer-grid {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
	gap: 2rem;
	margin-bottom: 3rem;
}

.footer-section h4 {
	font-size: 1.1rem;
	margin-bottom: 1.5rem;
	position: relative;
	padding-bottom: 0.75rem;
}

.footer-section h4::after {
	content: "";
	position: absolute;
	bottom: 0;
	left: 0;
	width: 40px;
	height: 2px;
	background: #2d3436;
}

.footer-links {
	list-style: none;
}

.footer-links li {
	margin-bottom: 0.8rem;
}

.footer-links a {
	color: #636e72;
	text-decoration: none;
	transition: all 0.2s ease;
	position: relative;
}

.footer-links a::before {
	content: "";
	position: absolute;
	bottom: -2px;
	left: 0;
	width: 0;
	height: 1px;
	background: #2d3436;
	transition: width 0.3s ease;
}

.footer-links a:hover::before {
	width: 100%;
}

.contact-info {
	margin-top: 1.5rem;
}

.contact-item {
	display: flex;
	align-items: center;
	gap: 0.8rem;
	margin-bottom: 1rem;
	color: #636e72;
}

.social-links {
	display: flex;
	gap: 1rem;
	margin-top: 1.5rem;
}

.social-link {
	color: #2d3436;
	font-size: 1.2rem;
	transition: transform 0.3s ease;
}

.social-link:hover {
	transform: translateY(-3px);
}

.footer-bottom {
	border-top: 1px solid #e0e0e0;
	padding-top: 2rem;
	margin-top: 3rem;
	display: flex;
	flex-wrap: wrap;
	justify-content: space-between;
	gap: 1rem;
}

.legal-links {
	display: flex;
	gap: 1.5rem;
}

.legal-links a {
	color: #636e72;
	text-decoration: none;
	transition: color 0.2s ease;
}

.legal-links a:hover {
	color: #2d3436;
}

@media (max-width: 768px) {
	.footer-grid {
		grid-template-columns: 1fr;
	}

	.footer-section h4::after {
		left: 50%;
		transform: translateX(-50%);
	}

	.footer-bottom {
		flex-direction: column;
		text-align: center;
	}

	.legal-links {
		justify-content: center;
	}
}

.newsletter-form {
	margin-top: 1.5rem;
	display: flex;
	gap: 0.5rem;
}

.newsletter-input {
	flex: 1;
	padding: 0.8rem;
	border: 1px solid #e0e0e0;
	border-radius: 4px;
	font-size: 0.9rem;
}

.newsletter-button {
	padding: 0.8rem 1.5rem;
	background: #2d3436;
	color: white;
	border: none;
	border-radius: 4px;
	cursor: pointer;
	transition: opacity 0.3s ease;
}

.newsletter-button:hover {
	opacity: 0.9;
}

script:
// Smooth scroll for anchor links

document.querySelectorAll('a[href^="#"]').forEach((anchor) => {
	anchor.addEventListener("click", function (e) {
		e.preventDefault();
		const target = document.querySelector(this.getAttribute("href"));
		if (target) {
			target.scrollIntoView({
				behavior: "smooth",
				block: "start",
		});
		}
	});
});

// Newsletter form handling
document.querySelector(".newsletter-form").addEventListener("submit", function (e) {
	e.preventDefault();
	const email = this.querySelector("input").value;
	if (validateEmail(email)) {
		alert(`Thanks for subscribing! We'll send updates to ${email}`);
		this.reset();
	} else {
		alert("Please enter a valid email address");
	}
});

function validateEmail(email) {
	const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
	return re.test(email);
}
