style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", sans-serif;
}

.footer {
	background: #1a1a1a;
	color: #ffffff;
	padding: 50px 0 30px;
}

.footer-container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 20px;
	display: flex;
	flex-wrap: wrap;
	gap: 40px;
}

.footer-column {
	flex: 1 1 250px;
	padding: 15px;
}

.footer-title {
	color: #00b4d8;
	font-size: 1.2rem;
	margin-bottom: 25px;
	padding-bottom: 10px;
	border-bottom: 2px solid #00b4d8;
}

.contact-list {
	list-style: none;
}

.contact-list li {
	margin-bottom: 15px;
	display: flex;
	align-items: center;
	gap: 12px;
}

.contact-list i {
	color: #00b4d8;
	min-width: 25px;
}

.footer-links {
	list-style: none;
}

.footer-links li {
	margin-bottom: 12px;
}

.footer-links a {
	color: #cccccc;
	text-decoration: none;
	transition: color 0.3s ease;
}

.footer-links a:hover {
	color: #00b4d8;
}

.business-hours {
	color: #cccccc;
	line-height: 1.8;
}

.social-container {
	display: flex;
	gap: 15px;
	margin-top: 20px;
}

.social-link {
	width: 40px;
	height: 40px;
	border: 1px solid #00b4d8;
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	transition: all 0.3s ease;
}

.social-link:hover {
	background: #00b4d8;
}

.footer-bottom {
	margin-top: 50px;
	padding-top: 30px;
	border-top: 1px solid #333333;
	text-align: center;
}

.legal-links {
	display: flex;
	justify-content: center;
	gap: 25px;
	margin-bottom: 15px;
}

.legal-links a {
	color: #cccccc;
	text-decoration: none;
	font-size: 0.9rem;
	transition: color 0.3s ease;
}

.legal-links a:hover {
	color: #00b4d8;
}

.copyright {
	color: #999999;
	font-size: 0.9rem;
}

@media (max-width: 768px) {
	.footer-container {
		flex-direction: column;
		text-align: center;
	}

	.contact-list li {
		justify-content: center;
	}

	.social-container {
		justify-content: center;
	}
}

script:
// Add smooth scroll for anchor links

document.querySelectorAll('a[href^="#"]').forEach((anchor) => {
	anchor.addEventListener("click", function (e) {
		e.preventDefault();
		const target = document.querySelector(this.getAttribute("href"));
		if (target) {
			target.scrollIntoView({
				behavior: "smooth",
				block: "start",
		});
		}
	});
});
