style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", sans-serif;
}

.footer {
	background: #0a2540;
	color: #ffffff;
	padding: 60px 0 30px;
	margin-top: auto;
}

.footer-container {
	max-width: 1280px;
	margin: 0 auto;
	padding: 0 32px;
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
	gap: 40px;
}

.footer-section {
	margin-bottom: 30px;
}

.footer-heading {
	color: #00ff88;
	font-size: 1.2rem;
	margin-bottom: 25px;
	position: relative;
	padding-bottom: 10px;
}

.footer-heading::after {
	content: "";
	position: absolute;
	bottom: 0;
	left: 0;
	width: 40px;
	height: 2px;
	background: #00ff88;
}

.footer-links {
	list-style: none;
}

.footer-links li {
	margin-bottom: 12px;
}

.footer-links a {
	color: #a3b3c6;
	text-decoration: none;
	transition: all 0.3s ease;
}

.footer-links a:hover {
	color: #00ff88;
	padding-left: 5px;
}

.contact-info {
	list-style: none;
}

.contact-info li {
	margin-bottom: 15px;
	display: flex;
	align-items: center;
	gap: 12px;
	color: #a3b3c6;
}

.contact-info i {
	color: #00ff88;
	width: 20px;
}

.newsletter-form {
	margin-top: 20px;
}

.newsletter-input {
	width: 100%;
	padding: 12px 20px;
	margin-bottom: 15px;
	border: 1px solid #1a365d;
	border-radius: 6px;
	background: #0d2a49;
	color: #ffffff;
	transition: all 0.3s ease;
}

.newsletter-input:focus {
	outline: none;
	border-color: #00ff88;
}

.newsletter-btn {
	background: #00ff88;
	color: #0a2540;
	padding: 12px 30px;
	border: none;
	border-radius: 6px;
	cursor: pointer;
	font-weight: 600;
	transition: all 0.3s ease;
}

.newsletter-btn:hover {
	background: #00cc6a;
}

.social-links {
	display: flex;
	gap: 15px;
	margin-top: 25px;
}

.social-links a {
	width: 40px;
	height: 40px;
	background: #0d2a49;
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	color: #ffffff;
	transition: all 0.3s ease;
}

.social-links a:hover {
	background: #00ff88;
	transform: translateY(-3px);
}

.footer-bottom {
	margin-top: 50px;
	padding-top: 30px;
	border-top: 1px solid #1a365d;
	text-align: center;
}

.legal-links {
	display: flex;
	justify-content: center;
	gap: 25px;
	margin-bottom: 15px;
}

.legal-links a {
	color: #a3b3c6;
	text-decoration: none;
	font-size: 0.9rem;
	transition: all 0.3s ease;
}

.legal-links a:hover {
	color: #00ff88;
}

.copyright {
	color: #6b7c93;
	font-size: 0.9rem;
}

@media (max-width: 768px) {
	.footer-container {
		grid-template-columns: 1fr;
		text-align: center;
	}

	.footer-heading::after {
		left: 50%;
		transform: translateX(-50%);
	}

	.contact-info li {
		justify-content: center;
	}

	.social-links {
		justify-content: center;
	}
}

script:
document.getElementById("newsletterForm").addEventListener("submit", function (e) {
	e.preventDefault();
	const emailInput = this.querySelector("input");
	if (validateEmail(emailInput.value)) {
		alert("Thank you for subscribing!");
		emailInput.value = "";
	} else {
		alert("Please enter a valid email address");
	}
});

function validateEmail(email) {
	const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
	return re.test(email);
}

// Smooth Scroll for Anchor Links

document.querySelectorAll('a[href^="#"]').forEach((anchor) => {
	anchor.addEventListener("click", function (e) {
		e.preventDefault();
		const target = document.querySelector(this.getAttribute("href"));
		if (target) {
			target.scrollIntoView({
				behavior: "smooth",
				block: "start",
			});
		}
	});
});
