style:
:root {
	--primary-color: #2b2d42;
	--accent-color: #ef233c;
	--text-light: #edf2f4;
	--text-dark: #2b2d42;
	--bg-light: #edf2f4;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", sans-serif;
}

.footer {
	background: var(--primary-color);
	color: var(--text-light);
	padding: 4rem 0 2rem;
	margin-top: auto;
}

.footer-container {
	max-width: 1280px;
	margin: 0 auto;
	padding: 0 2rem;
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
	gap: 3rem;
}

.footer-section {
	margin-bottom: 2rem;
}

.footer-heading {
	color: var(--accent-color);
	font-size: 1.25rem;
	margin-bottom: 1.5rem;
	position: relative;
	padding-bottom: 0.5rem;
}

.footer-heading::after {
	content: "";
	position: absolute;
	bottom: 0;
	left: 0;
	width: 40px;
	height: 2px;
	background: var(--accent-color);
}

.footer-links {
	list-style: none;
}

.footer-links li {
	margin-bottom: 0.75rem;
}

.footer-links a {
	color: var(--text-light);
	text-decoration: none;
	transition: all 0.3s ease;
	padding: 0.25rem 0;
	display: inline-block;
}

.footer-links a:hover,
.footer-links a:focus {
	color: var(--accent-color);
	transform: translateX(5px);
}

.contact-info {
	list-style: none;
}

.contact-info li {
	margin-bottom: 1rem;
	display: flex;
	align-items: center;
	gap: 1rem;
}

.contact-info i {
	color: var(--accent-color);
	width: 1.5rem;
	text-align: center;
}

.social-links {
	display: flex;
	gap: 1rem;
	margin-top: 1.5rem;
}

.social-link {
	width: 40px;
	height: 40px;
	border: 2px solid var(--text-light);
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	transition: all 0.3s ease;
}

.social-link:hover,
.social-link:focus {
	border-color: var(--accent-color);
	background: var(--accent-color);
}

.newsletter-form {
	margin-top: 1.5rem;
}

.newsletter-input {
	width: 100%;
	padding: 0.75rem 1rem;
	margin-bottom: 1rem;
	border: 2px solid var(--text-light);
	border-radius: 4px;
	background: transparent;
	color: var(--text-light);
	transition: all 0.3s ease;
}

.newsletter-input:focus {
	outline: none;
	border-color: var(--accent-color);
}

.newsletter-btn {
	background: var(--accent-color);
	color: var(--text-light);
	padding: 0.75rem 2rem;
	border: none;
	border-radius: 4px;
	cursor: pointer;
	font-weight: 600;
	transition: all 0.3s ease;
}

.newsletter-btn:hover,
.newsletter-btn:focus {
	background: #d90429;
}

.footer-bottom {
	margin-top: 3rem;
	padding-top: 2rem;
	border-top: 1px solid rgba(237, 242, 244, 0.1);
	text-align: center;
}

.legal-links {
	display: flex;
	justify-content: center;
	flex-wrap: wrap;
	gap: 1.5rem;
	margin-bottom: 1rem;
}

.legal-links a {
	color: var(--text-light);
	text-decoration: none;
	transition: all 0.3s ease;
}

.legal-links a:hover,
.legal-links a:focus {
	color: var(--accent-color);
}

.copyright {
	color: rgba(237, 242, 244, 0.8);
	font-size: 0.9rem;
}

@media (max-width: 768px) {
	.footer-container {
		grid-template-columns: 1fr;
		text-align: center;
	}

	.footer-heading::after {
		left: 50%;
		transform: translateX(-50%);
	}

	.contact-info li {
		justify-content: center;
	}

	.social-links {
		justify-content: center;
	}
}

/* Accessibility */

a:focus,
button:focus {
	outline: 2px solid var(--accent-color);
	outline-offset: 2px;
}

script:
document.getElementById("newsletterForm").addEventListener("submit", function (e) {
	e.preventDefault();
	const email = this.querySelector("input").value;
	if (validateEmail(email)) {
		alert("Thank you for subscribing!");
		this.reset();
	} else {
		alert("Please enter a valid email address");
	}
});

function validateEmail(email) {
	const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
	return re.test(email);
}

// Add aria-current to active page
const currentPath = window.location.pathname;
document.querySelectorAll(".footer-links a").forEach((link) => {
	if (link.getAttribute("href") === currentPath) {
		link.setAttribute("aria-current", "page");
	}
});
