style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", sans-serif;
}

.footer {
	background: #111827;
	color: #fff;
	padding: 4rem 0 2rem;
}

.footer-container {
	max-width: 1280px;
	margin: 0 auto;
	padding: 0 2rem;
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
	gap: 3rem;
}

.footer-section {
	margin-bottom: 2rem;
}

.footer-title {
	color: #3b82f6;
	font-size: 1.1rem;
	margin-bottom: 1.5rem;
	text-transform: uppercase;
	letter-spacing: 1px;
}

.footer-links {
	list-style: none;
}

.footer-links li {
	margin-bottom: 0.8rem;
}

.footer-links a {
	color: #9ca3af;
	text-decoration: none;
	transition: all 0.3s ease;
}

.footer-links a:hover {
	color: #3b82f6;
	padding-left: 5px;
}

.contact-info {
	list-style: none;
}

.contact-info li {
	margin-bottom: 1rem;
	display: flex;
	align-items: center;
	gap: 1rem;
	color: #9ca3af;
}

.contact-info i {
	color: #3b82f6;
	width: 1.5rem;
}

.badge-container {
	display: flex;
	gap: 1rem;
	flex-wrap: wrap;
	margin-top: 1.5rem;
}

.badge {
	background: #1f2937;
	padding: 0.5rem 1rem;
	border-radius: 4px;
	font-size: 0.9rem;
}

.footer-bottom {
	margin-top: 3rem;
	padding-top: 2rem;
	border-top: 1px solid #374151;
	display: flex;
	flex-wrap: wrap;
	justify-content: space-between;
	gap: 1rem;
}

.legal-links {
	display: flex;
	gap: 1.5rem;
}

.legal-links a {
	color: #9ca3af;
	text-decoration: none;
	font-size: 0.9rem;
	transition: all 0.3s ease;
}

.legal-links a:hover {
	color: #3b82f6;
}

.language-selector select {
	background: transparent;
	color: #fff;
	border: 1px solid #374151;
	padding: 0.5rem;
	border-radius: 4px;
}

@media (max-width: 768px) {
	.footer-container {
		grid-template-columns: 1fr;
		text-align: center;
	}

	.contact-info li {
		justify-content: center;
	}

	.badge-container {
		justify-content: center;
	}

	.footer-bottom {
		flex-direction: column;
		align-items: center;
	}

	.legal-links {
		flex-wrap: wrap;
		justify-content: center;
	}
}

script:
// Smooth scroll for anchor links
document.querySelectorAll('a[href^="#"]').forEach((anchor) => {
	anchor.addEventListener("click", function (e) {
		e.preventDefault();
		const target = document.querySelector(this.getAttribute("href"));
		if (target) {
			target.scrollIntoView({
				behavior: "smooth",
				block: "start",
			});
		}
	});
});

// Newsletter form handling
document.querySelector("form").addEventListener("submit", function (e) {
	e.preventDefault();
	const email = this.querySelector("input").value;
	if (validateEmail(email)) {
		alert("Thanks for subscribing!");
		this.reset();
	} else {
		alert("Please enter a valid email address");
	}
});

function validateEmail(email) {
	const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
	return re.test(email);
}
