style:
:root {
  --bg: #ffffff;
  --text: #1e293b;
  --primary: #3b82f6;
  --secondary: #64748b;
  --border: #e2e8f0;
  --footer-bg: #f8fafc;
}

[data-theme="dark"] {
  --bg: #0f172a;
  --text: #f8fafc;
  --primary: #60a5fa;
  --secondary: #94a3b8;
  --border: #334155;
  --footer-bg: #1e293b;
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  transition: background 0.3s, color 0.3s, border-color 0.3s;
}

body {
  background: var(--bg);
  color: var(--text);
  font-family: "Inter", sans-serif;
  min-height: 100vh;
}

.footer {
  background: var(--footer-bg);
  padding: 4rem 0 2rem;
  border-top: 1px solid var(--border);
}

.footer-container {
  max-width: 1280px;
  margin: 0 auto;
  padding: 0 2rem;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 3rem;
}

.footer-section {
  margin-bottom: 2rem;
}

.footer-title {
  color: var(--primary);
  font-size: 1rem;
  margin-bottom: 1.5rem;
  text-transform: uppercase;
  letter-spacing: 1px;
}

.footer-links {
  list-style: none;
}

.footer-links li {
  margin-bottom: 0.8rem;
}

.footer-links a {
  color: var(--secondary);
  text-decoration: none;
  display: flex;
  align-items: center;
  gap: 0.5rem;
}

.footer-links a:hover {
  color: var(--primary);
}

.contact-info {
  list-style: none;
}

.contact-info li {
  margin-bottom: 1rem;
  display: flex;
  align-items: center;
  gap: 1rem;
  color: var(--secondary);
}

.controls {
  display: flex;
  gap: 1rem;
  margin-top: 1.5rem;
}

.theme-toggle,
.language-select {
  padding: 0.5rem 1rem;
  border-radius: 4px;
  background: var(--bg);
  border: 1px solid var(--border);
  color: var(--text);
  cursor: pointer;
}

.theme-toggle:hover {
  border-color: var(--primary);
}

.footer-bottom {
  margin-top: 3rem;
  padding-top: 2rem;
  border-top: 1px solid var(--border);
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  gap: 1rem;
}

.legal-links {
  display: flex;
  gap: 1.5rem;
}

.legal-links a {
  color: var(--secondary);
  text-decoration: none;
  font-size: 0.9rem;
}

.legal-links a:hover {
  color: var(--primary);
}

.cookie-consent {
  position: fixed;
  bottom: 1rem;
  left: 50%;
  transform: translateX(-50%);
  background: var(--bg);
  padding: 1rem;
  border-radius: 8px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  display: none;
  max-width: 90%;
  border: 1px solid var(--border);
}

@media (max-width: 768px) {
  .footer-container {
    grid-template-columns: 1fr;
    text-align: center;
  }

  .footer-links a {
    justify-content: center;
  }

  .controls {
    justify-content: center;
  }

  .footer-bottom {
    flex-direction: column;
    align-items: center;
  }
}

script:
// Theme Toggle
const themeToggle = document.getElementById("themeToggle");
let isDark = localStorage.getItem("theme") === "dark";

function updateTheme() {
  document.documentElement.setAttribute("data-theme", isDark ? "dark" : "light");
  themeToggle.innerHTML = isDark
    ? '<i class="fas fa-sun"></i> Theme'
    : '<i class="fas fa-moon"></i> Theme';
  localStorage.setItem("theme", isDark ? "dark" : "light");
}

themeToggle.addEventListener("click", () => {
  isDark = !isDark;
  updateTheme();
});

// Initialize theme
updateTheme();

// Cookie Consent
function showCookieConsent() {
  if (!localStorage.getItem("cookiesAccepted")) {
    document.getElementById("cookieConsent").style.display = "block";
  }
}

function acceptCookies() {
  localStorage.setItem("cookiesAccepted", "true");
  document.getElementById("cookieConsent").style.display = "none";
}

function managePreferences() {
  // Add preference management logic
  alert("Cookie preferences management");
}

// Show cookie consent on page load
setTimeout(showCookieConsent, 2000);

// Language Selector
document.querySelector(".language-select").addEventListener("change", (e) => {
  // Add language change logic
  console.log("Selected language:", e.target.value);
});
