style:
:root {
	--primary-color: #224f34;
	--secondary-color: #a3f3be;
	--text-color: #ffffff;
	--hover-color: #cccccc;
	--font-heading: "Roboto Slab", serif;
	--font-body: "Poppins", sans-serif;
}

body {
	margin: 0;
	font-family: var(--font-body);
	background-color: #f5f5f5;
}

.footer-section {
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: space-around;
	width: 100%;
	background-color: var(--primary-color);
	color: var(--text-color);
	padding: 50px 0;
}

.footer-container {
	display: flex;
	justify-content: space-around;
	align-items: flex-start;
	width: 80%;
	flex-wrap: wrap;
}

.footer-left h2 {
	font-family: "Elephant", serif;
	font-size: 50px;
	line-height: 61px;
}

.footer-left p {
	font-family: var(--font-heading);
	font-size: 12px;
	line-height: 29px;
	text-transform: capitalize;
}

.social-icons a {
	width: 38px;
	height: 38px;
	margin-right: 15px;
	display: inline-block;
}

.footer-links {
	display: flex;
	flex-direction: column;
	gap: 10px;
	min-width: 150px;
}

.footer-links h3 {
	font-family: var(--font-heading);
	font-size: 18px;
	text-transform: uppercase;
}

.footer-links ul {
	list-style: none;
	padding: 0;
}

.footer-links li {
	margin-bottom: 10px;
}

.footer-links a {
	font-family: var(--font-body);
	font-size: 14px;
	text-transform: capitalize;
	color: var(--secondary-color);
	text-decoration: none;
	transition: color 0.3s;
}

.footer-links a:hover {
	color: var(--hover-color);
}

.footer-right h3 {
	font-family: var(--font-heading);
	font-size: 22px;
	text-transform: uppercase;
	margin-bottom: 15px;
}

.newsletter-form {
	display: flex;
	align-items: center;
}

.newsletter-form input {
	padding: 8px 15px;
	border: 1px solid var(--secondary-color);
	background-color: transparent;
	color: var(--text-color);
	border-radius: 3px 0 0 3px;
	width: 320px;
	font-size: 16px;
	transition: border-color 0.3s ease;
}

.newsletter-form button {
	padding: 8px 15px;
	background-color: var(--secondary-color);
	border: 1px solid var(--secondary-color);
	border-radius: 0 3px 3px 0;
	cursor: pointer;
	font-family: var(--font-body);
	font-size: 18px;
	text-transform: uppercase;
	color: var(--primary-color);
	transition: background-color 0.3s ease;
}

.newsletter-form button:hover {
	background-color: #88b98a;
}

.footer-bottom {
	display: flex;
	align-items: center;
	justify-content: space-between;
	width: 80%;
	margin-top: 20px;
	flex-wrap: wrap;
}

.footer-bottom hr {
	width: 75%;
	border: 2px solid var(--secondary-color);
	border-radius: 5px;
}

.footer-bottom ul {
	display: flex;
	gap: 20px;
	list-style: none;
	padding: 0;
}

.footer-bottom a {
	font-family: var(--font-body);
	font-size: 16px;
	color: var(--text-color);
	text-decoration: none;
	transition: color 0.3s;
}

.footer-bottom a:hover {
	color: var(--hover-color);
}

@media (max-width: 768px) {
	.footer-section {
		padding: 30px 0;
	}

	.footer-container {
		flex-direction: column;
		align-items: center;
		text-align: center;
	}

	.footer-links {
		margin-top: 20px;
	}

	.footer-right {
		margin-top: 20px;
	}

	.footer-bottom {
		flex-direction: column;
		text-align: center;
	}

	.footer-bottom hr {
		display: none;
	}

	.footer-bottom ul {
		justify-content: center;
		flex-wrap: wrap;
		gap: 10px;
	}
}
