style:
:root {
  --bg: #ffffff;
  --text: #1a1a1a;
  --primary: #2563eb;
  --secondary: #4b5563;
  --border: #e5e7eb;
  --footer-bg: #f3f4f6;
  --footer-height: 400px;
}

[data-theme="dark"] {
  --bg: #0f172a;
  --text: #f8fafc;
  --primary: #3b82f6;
  --secondary: #94a3b8;
  --border: #334155;
  --footer-bg: #1e293b;
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  transition: background 0.3s, color 0.3s, transform 0.2s;
}

body {
  display: flex;
  flex-direction: column;
  min-height: 100vh;
  background: var(--bg);
  color: var(--text);
  font-family: "Inter", sans-serif;
}

main {
  flex: 1;
  padding: 2rem;
}

.footer {
  background: var(--footer-bg);
  border-top: 1px solid var(--border);
  margin-top: auto;
}

.footer-container {
  max-width: 1280px;
  margin: 0 auto;
  padding: 4rem 2rem 2rem;
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 3rem;
}

.footer-section {
  position: relative;
}

.section-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1.5rem;
}

.section-title {
  color: var(--primary);
  font-size: 1.1rem;
  font-weight: 600;
}

.collapse-btn {
  display: none;
  background: none;
  border: none;
  color: var(--secondary);
  cursor: pointer;
}

.footer-links {
  list-style: none;
  display: grid;
  gap: 0.8rem;
}

.footer-links a {
  color: var(--secondary);
  text-decoration: none;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.5rem;
  border-radius: 4px;
}

.footer-links a:hover {
  background: rgba(59, 130, 246, 0.1);
  color: var(--primary);
}

.newsletter-form {
  display: grid;
  gap: 1rem;
}

.newsletter-input {
  padding: 0.75rem;
  border: 1px solid var(--border);
  border-radius: 6px;
  background: transparent;
  color: var(--text);
}

.theme-toggle {
  position: relative;
  width: 48px;
  height: 24px;
  background: var(--secondary);
  border-radius: 12px;
  cursor: pointer;
}

.theme-toggle::after {
  content: "";
  position: absolute;
  top: 2px;
  left: 2px;
  width: 20px;
  height: 20px;
  background: var(--bg);
  border-radius: 50%;
  transition: transform 0.3s;
}

[data-theme="dark"] .theme-toggle::after {
  transform: translateX(24px);
}

.footer-bottom {
  border-top: 1px solid var(--border);
  padding: 2rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.back-to-top {
  position: fixed;
  bottom: 2rem;
  right: 2rem;
  background: var(--primary);
  color: white;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  opacity: 0;
  transform: translateY(20px);
  transition: all 0.3s;
}

.back-to-top.visible {
  opacity: 1;
  transform: translateY(0);
}

@media (max-width: 768px) {
  .footer-container {
    grid-template-columns: 1fr;
    padding: 2rem 1rem;
  }

  .collapse-btn {
    display: block;
  }

  .footer-links {
    max-height: 0;
    overflow: hidden;
    transition: max-height 0.3s ease-out;
  }

  .footer-section.active .footer-links {
    max-height: 500px;
  }
}

script:
// Theme Toggle
let isDark = localStorage.getItem("theme") === "dark" || (window.matchMedia("(prefers-color-scheme: dark)").matches && !localStorage.getItem("theme"));

function toggleTheme() {
  isDark = !isDark;
  document.documentElement.setAttribute("data-theme", isDark ? "dark" : "light");
  localStorage.setItem("theme", isDark ? "dark" : "light");
}

// Initialize theme
document.documentElement.setAttribute("data-theme", isDark ? "dark" : "light");

// Mobile Section Toggle
function toggleSection(btn) {
  const section = btn.closest(".footer-section");
  section.classList.toggle("active");
  btn.textContent = section.classList.contains("active") ? "−" : "+";
}

// Back to Top Button
window.addEventListener("scroll", () => {
  const btn = document.getElementById("backToTop");
  btn.classList.toggle("visible", window.scrollY > 300);
});

function scrollToTop() {
  window.scrollTo({ top: 0, behavior: "smooth" });
}

// Newsletter Form
document.getElementById("newsletterForm").addEventListener("submit", (e) => {
  e.preventDefault();
  const email = e.target.querySelector("input").value;
  if (/^[^s@]+@[^s@]+.[^s@]+$/.test(email)) {
    alert("Thank you for subscribing!");
    e.target.reset();
  } else {
    alert("Please enter a valid email address");
  }
});

// Dynamic Copyright Year
document.getElementById("currentYear").textContent = new Date().getFullYear();

// Close mobile sections when clicking outside
document.addEventListener("click", (e) => {
  if (window.innerWidth <= 768 && !e.target.closest(".footer-section")) {
    document.querySelectorAll(".footer-section").forEach((section) => {
      section.classList.remove("active");
      section.querySelector(".collapse-btn").textContent = "+";
    });
  }
});
