style:
:root {
	--bg: #ffffff;
	--text: #1a1a1a;
	--primary: #4f46e5;
	--secondary: #64748b;
	--border: #e2e8f0;
	--footer-bg: #f8fafc;
	--transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
}

[data-theme="dark"] {
	--bg: #0f172a;
	--text: #f8fafc;
	--primary: #818cf8;
	--secondary: #94a3b8;
	--border: #334155;
	--footer-bg: #1e293b;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: var(--transition);
}

body {
	background: var(--bg);
	color: var(--text);
	font-family: "Inter", sans-serif;
	min-height: 100vh;
	display: flex;
	flex-direction: column;
}

.footer {
	background: var(--footer-bg);
	border-top: 1px solid var(--border);
	margin-top: auto;
}

.footer-container {
	max-width: 1440px;
	margin: 0 auto;
	padding: 4rem 2rem;
	display: grid;
	grid-template-columns: 1fr 1fr;
	gap: 4rem;
}

.footer-section {
	display: grid;
	gap: 2rem;
}

.contact-card {
	background: var(--bg);
	padding: 2rem;
	border-radius: 1rem;
	border: 1px solid var(--border);
	box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
}

.contact-item {
	display: flex;
	align-items: center;
	gap: 1rem;
	margin-bottom: 1.5rem;
	color: var(--secondary);
}

.contact-item i {
	color: var(--primary);
	width: 1.5rem;
}

.social-links {
	display: flex;
	gap: 1rem;
	margin-top: 1rem;
}

.social-link {
	width: 40px;
	height: 40px;
	border: 1px solid var(--border);
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	color: var(--secondary);
}

.social-link:hover {
	transform: translateY(-3px);
	border-color: var(--primary);
	color: var(--primary);
}

.newsletter-form {
	display: grid;
	gap: 1rem;
}

.input-group {
	position: relative;
}

.newsletter-input {
	width: 100%;
	padding: 1rem;
	border: 1px solid var(--border);
	border-radius: 0.5rem;
	background: var(--bg);
	color: var(--text);
}

.newsletter-input:focus {
	outline: none;
	border-color: var(--primary);
	box-shadow: 0 0 0 3px rgba(79, 70, 229, 0.2);
}

.legal-links {
	display: grid;
	grid-template-columns: repeat(2, 1fr);
	gap: 1rem;
}

.legal-links a {
	color: var(--secondary);
	text-decoration: none;
	padding: 0.5rem;
	border-radius: 0.5rem;
}

.legal-links a:hover {
	background: rgba(79, 70, 229, 0.1);
	color: var(--primary);
}

.footer-bottom {
	border-top: 1px solid var(--border);
	padding: 2rem;
	display: flex;
	justify-content: space-between;
	align-items: center;
}

.theme-toggle {
	position: relative;
	width: 48px;
	height: 24px;
	background: var(--secondary);
	border-radius: 12px;
	cursor: pointer;
	transition: background 0.3s;
}

.theme-toggle::after {
	content: "";
	position: absolute;
	top: 2px;
	left: 2px;
	width: 20px;
	height: 20px;
	background: var(--bg);
	border-radius: 50%;
	transition: transform 0.3s;
}

[data-theme="dark"] .theme-toggle::after {
	transform: translateX(24px);
}

.theme-loading {
	position: fixed;
	top: 0;
	left: 0;
	width: 100%;
	height: 3px;
	background: var(--primary);
	transform: scaleX(0);
	transform-origin: left;
	z-index: 1000;
}

@media (max-width: 768px) {
	.footer-container {
		grid-template-columns: 1fr;
		padding: 2rem 1rem;
	}

	.legal-links {
		grid-template-columns: 1fr;
	}
}

script:
// Theme Management with Loading Indicator
const themeToggle = document.getElementById("themeToggle");
const themeLoader = document.getElementById("themeLoader");
let isDark = localStorage.getItem("theme") === "dark";

function toggleTheme() {
	themeLoader.style.transform = "scaleX(1)";
	setTimeout(() => {
		isDark = !isDark;
		document.documentElement.setAttribute("data-theme", isDark ? "dark" : "light");
		localStorage.setItem("theme", isDark ? "dark" : "light");
		themeLoader.style.transform = "scaleX(0)";
	}, 600);
}

themeToggle.addEventListener("click", toggleTheme);
document.documentElement.setAttribute("data-theme", isDark ? "dark" : "light");

// Newsletter Form Validation
const newsletterForm = document.getElementById("newsletterForm");
newsletterForm.addEventListener("submit", (e) => {
	e.preventDefault();
	const email = newsletterForm.querySelector("input").value;
	if (validateEmail(email)) {
		showSuccess();
		newsletterForm.reset();
	} else {
		showError();
	}
});

function validateEmail(email) {
	return /^[^\s@]+@[\s@]+\.[^\s@]+$/.test(email);
}

function showSuccess() {
	const btn = newsletterForm.querySelector("button");
	btn.innerHTML = '<i class="fas fa-check"></i> Subscribed!';
	btn.style.backgroundColor = "#10b981";
	setTimeout(() => {
		btn.innerHTML = "Subscribe to Newsletter";
		btn.style.backgroundColor = "";
	}, 2000);
}

function showError() {
	const input = newsletterForm.querySelector("input");
	input.style.borderColor = "#ef4444";
	input.focus();
	setTimeout(() => (input.style.borderColor = ""), 2000);
}

// Dynamic Year
document.getElementById("currentYear").textContent = new Date().getFullYear();

// Smooth Scroll
function scrollToTop() {
	window.scrollTo({
		top: 0,
		behavior: "smooth",
	});
}

// Intersection Observer for Animations
const observer = new IntersectionObserver((entries) => {
	entries.forEach((entry) => {
		if (entry.isIntersecting) {
			entry.target.style.opacity = 1;
			entry.target.style.transform = "translateY(0)";
		}
	});
});

document.querySelectorAll(".contact-card, .legal-links").forEach((el) => {
	el.style.opacity = 0;
	el.style.transform = "translateY(20px)";
	observer.observe(el);
});
