style:
:root {
	--primary: hsl(260, 100%, 70%);
	--secondary: hsl(180, 100%, 50%);
	--bg: #0a0a0a;
	--text: #ffffff;
	--glass: rgba(255, 255, 255, 0.05);
}

[data-theme="light"] {
	--bg: #f0f0f0;
	--text: #0a0a0a;
	--glass: rgba(0, 0, 0, 0.05);
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

body {
	font-family: "Inter", sans-serif;
	background: var(--bg);
	color: var(--text);
	min-height: 200vh;
}

.footer {
	position: relative;
	padding: 6rem 0 3rem;
	overflow: hidden;
}

.holographic-effect {
	position: absolute;
	width: 200vw;
	height: 200vh;
	background: linear-gradient(45deg, var(--primary), var(--secondary), var(--primary));
	opacity: 0.1;
	mix-blend-mode: screen;
	animation: move 20s linear infinite;
	pointer-events: none;
}

.footer-container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 2rem;
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
	gap: 2rem;
	position: relative;
}

.footer-section {
	padding: 2rem;
	backdrop-filter: blur(20px);
	background: var(--glass);
	border-radius: 1rem;
	border: 1px solid rgba(255, 255, 255, 0.1);
	transform: translateY(20px);
	opacity: 0;
	transition: all 0.6s ease;
}

.footer-title {
	font-size: 1.25rem;
	margin-bottom: 1.5rem;
	position: relative;
	background: linear-gradient(45deg, var(--primary), var(--secondary));
	-webkit-background-clip: text;
	 ackground-clip: text;
	-webkit-text-fill-color: transparent;
}

.footer-links {
	list-style: none;
}

.footer-links li {
	margin-bottom: 1rem;
}

.footer-links a {
	color: var(--text);
	text-decoration: none;
	display: flex;
	align-items: center;
	gap: 0.8rem;
	padding: 0.5rem;
	border-radius: 0.5rem;
	transition: all 0.3s ease;
}

.footer-links a:hover {
	background: rgba(255, 255, 255, 0.05);
	transform: translateX(10px);
}

.social-grid {
	display: grid;
	grid-template-columns: repeat(3, 1fr);
	gap: 1rem;
	margin-top: 2rem;
}

.social-link {
	padding: 1rem;
	border-radius: 0.5rem;
	background: var(--glass);
	text-align: center;
	transition: all 0.3s ease;
}

.social-link:hover {
	background: linear-gradient(45deg, var(--primary), var(--secondary));
	color: var(--bg);
}

.newsletter-form {
	margin-top: 2rem;
	position: relative;
}

.newsletter-input {
	width: 100%;
	padding: 1rem;
	background: var(--glass);
	border: 1px solid rgba(255, 255, 255, 0.1);
	border-radius: 0.5rem;
	color: var(--text);
}

.theme-toggle {
	position: fixed;
	bottom: 2rem;
	right: 2rem;
	width: 3.5rem;
	height: 3.5rem;
	border-radius: 50%;
	background: linear-gradient(45deg, var(--primary), var(--secondary));
	border: none;
	cursor: pointer;
	display: flex;
	align-items: center;
	justify-content: center;
	color: var(--bg);
	z-index: 1000;
}

@keyframes move {
	0% {
		transform: translate(-50%, -50%) rotate(0deg);
	}
	100% {
		transform: translate(0%, 0%) rotate(360deg);
	}
}

@media (max-width: 768px) {
	.footer-container {
		grid-template-columns: 1fr;
	}

	.footer-section {
		text-align: center;
	}

	.footer-links a {
		justify-content: center;
	}
}

script:
// Dynamic Theme Toggle
const themeToggle = document.getElementById("themeToggle");
let isDark = true;

themeToggle.addEventListener("click", () => {
	isDark = !isDark;
	document.documentElement.setAttribute("data-theme", isDark ? "" : "light");
	themeToggle.innerHTML = isDark ? '<i class="fas fa-sun"></i>' : '<i class="fas fa-moon"></i>';
});

// Intersection Observer
const observer = new IntersectionObserver(
	(entries) => {
		entries.forEach((entry) => {
			if (entry.isIntersecting) {
				entry.target.style.opacity = 1;
				entry.target.style.transform = "translateY(0)";
			}
		});
	},
	{ threshold: 0.1 },
);

document.querySelectorAll(".footer-section").forEach((section) => {
	observer.observe(section);
});

// Dynamic Color Shift
let hue = 260;
function updateColors() {
	hue = (hue + 0.5) % 360;
	document.documentElement.style.setProperty("--primary", `hsl(${hue}, 100%, 70%)`);
	requestAnimationFrame(updateColors);
}
updateColors();

// Form Handling
document.getElementById("newsletterForm").addEventListener("submit", (e) => {
	e.preventDefault();
	const email = e.target.querySelector("input").value;
	if (validateEmail(email)) {
		showNotification("Neural link established ðŸ§ âœ¨");
		e.target.reset();
	} else {
		showNotification("Interface protocol mismatch âš ï¸");
	}
});

function validateEmail(email) {
	return /^[^\s@]+@[^s@]+\.[^s@]+$/.test(email);
}

function showNotification(message) {
	const notification = document.createElement("div");
	notification.textContent = message;
	notification.style.position = "fixed";
	notification.style.bottom = "6rem";
	notification.style.right = "2rem";
	notification.style.padding = "1rem 2rem";
	notification.style.background = "var(--glass)";
	notification.style.color = "var(--text)";
	notification.style.borderRadius = "0.5rem";
	notification.style.backdropFilter = "blur(10px)";
	notification.style.border = "1px solid rgba(255, 255, 255, 0.1)";
	document.body.appendChild(notification);

	setTimeout(() => notification.remove(), 3000);
}
