style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Poppins", sans-serif;
}

body {
	min-height: 200vh; /* For demonstration scroll */
}

.footer {
	background: #0a0a0a;
	color: #ffffff;
	padding: 60px 0 30px;
}

.footer-container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 20px;
}

.footer-content {
	display: flex;
	flex-wrap: wrap;
	gap: 40px;
	margin-bottom: 50px;
}

.footer-col {
	flex: 1;
	min-width: 250px;
}

.footer-col h4 {
	font-size: 1.3rem;
	margin-bottom: 25px;
	position: relative;
	padding-bottom: 10px;
}

.footer-col h4::after {
	content: "";
	position: absolute;
	left: 0;
	bottom: 0;
	width: 40px;
	height: 2px;
	background: #ff4d4d;
}

.footer-links {
	list-style: none;
}

.footer-links li {
	margin-bottom: 12px;
}

.footer-links a {
	color: #b5b5b5;
	text-decoration: none;
	transition: all 0.3s ease;
	display: flex;
	align-items: center;
	gap: 8px;
}

.footer-links a:hover {
	color: #ffffff;
	transform: translateX(5px);
}

.newsletter-form {
	margin-top: 20px;
}

.newsletter-input {
	width: 100%;
	padding: 12px;
	background: #1a1a1a;
	border: 1px solid #333;
	color: #fff;
	margin-bottom: 15px;
}

.newsletter-btn {
	background: #ff4d4d;
	color: #fff;
	border: none;
	padding: 12px 25px;
	cursor: pointer;
	transition: all 0.3s ease;
}

.newsletter-btn:hover {
	background: #ff3333;
}

.social-media {
	margin-top: 30px;
	display: flex;
	gap: 15px;
}

.social-media a {
	width: 40px;
	height: 40px;
	background: #1a1a1a;
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	color: #fff;
	transition: all 0.3s ease;
}

.social-media a:hover {
	background: #ff4d4d;
	transform: translateY(-3px);
}

.footer-bottom {
	border-top: 1px solid #333;
	padding-top: 30px;
	text-align: center;
}

.payment-methods {
	margin-top: 20px;
	display: flex;
	gap: 15px;
	justify-content: center;
	flex-wrap: wrap;
}

.payment-methods img {
	width: 50px;
	filter: grayscale(100%);
	transition: filter 0.3s ease;
}

.payment-methods img:hover {
	filter: grayscale(0%);
}

@media (max-width: 768px) {
	.footer-col {
		flex: 100%;
		text-align: center;
	}

	.footer-col h4::after {
		left: 50%;
		transform: translateX(-50%);
	}

	.footer-links a {
		justify-content: center;
	}

	.social-media {
		justify-content: center;
	}
}

.footer-col {
	opacity: 0;
	transform: translateY(30px);
	transition: all 0.6s ease;
}

.footer-col.visible {
	opacity: 1;
	transform: translateY(0);
}

script:
// Newsletter Form Submission

document.getElementById("newsletterForm").addEventListener("submit", function (e) {
	e.preventDefault();
	const email = this.querySelector("input").value;
	alert(`Thank you for subscribing!\nWe've sent a confirmation to ${email}`);
	this.reset();
});

// Scroll Animation for Footer Columns
const footerCols = document.querySelectorAll(".footer-col");

const observer = new IntersectionObserver(
	(entries) => {
		entries.forEach((entry) => {
			if (entry.isIntersecting) {
				entry.target.classList.add("visible");
			}
		});
	},
	{ threshold: 0.1 },
);

footerCols.forEach((col) => observer.observe(col));

// Product Gallery Lightbox (example)
document.querySelectorAll(".product-gallery img").forEach((img) => {
	img.addEventListener("click", () => {
		// Add lightbox implementation here
		alert("Product image clicked - Add lightbox functionality");
	});
});
