style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Poppins", sans-serif;
}

body {
	min-height: 200vh; /* For demonstration */
}

.footer {
	background: linear-gradient(135deg, #2c3e50, #3498db);
	color: #fff;
	padding: 60px 0 30px;
	position: relative;
}

.footer-container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 20px;
	display: flex;
	flex-wrap: wrap;
	gap: 40px;
}

.footer-col {
	flex: 1 1 250px;
	padding: 15px;
}

.footer-logo {
	width: 180px;
	margin-bottom: 20px;
}

.footer-col h4 {
	font-size: 1.3rem;
	margin-bottom: 25px;
	position: relative;
}

.footer-col h4::after {
	content: "";
	position: absolute;
	left: 0;
	bottom: -10px;
	height: 2px;
	width: 50px;
	background: #e74c3c;
}

.footer-links {
	list-style: none;
}

.footer-links li {
	margin-bottom: 12px;
}

.footer-links a {
	color: #ecf0f1;
	text-decoration: none;
	transition: all 0.3s ease;
	display: flex;
	align-items: center;
	gap: 8px;
}

.footer-links a:hover {
	color: #e74c3c;
	transform: translateX(5px);
}

.social-box {
	display: flex;
	gap: 15px;
	margin-top: 25px;
}

.social-icon {
	width: 40px;
	height: 40px;
	background: rgba(255, 255, 255, 0.1);
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	transition: all 0.3s ease;
}

.social-icon:hover {
	background: #e74c3c;
	transform: translateY(-3px);
}

.newsletter-form {
	margin-top: 25px;
}

.newsletter-input {
	width: 100%;
	padding: 12px 20px;
	border: none;
	border-radius: 25px;
	background: rgba(255, 255, 255, 0.1);
	color: #fff;
	margin-bottom: 15px;
}

.newsletter-btn {
	background: #e74c3c;
	color: #fff;
	padding: 12px 30px;
	border: none;
	border-radius: 25px;
	cursor: pointer;
	transition: all 0.3s ease;
	width: 100%;
}

.newsletter-btn:hover {
	background: #c0392b;
	transform: translateY(-2px);
}

.footer-bottom {
	text-align: center;
	padding-top: 50px;
	margin-top: 40px;
	border-top: 1px solid rgba(255, 255, 255, 0.1);
}

.footer-bottom p {
	font-size: 0.9rem;
	opacity: 0.8;
}

.legal-links {
	display: flex;
	justify-content: center;
	gap: 20px;
	margin-top: 15px;
}

.legal-links a {
	color: #ecf0f1;
	text-decoration: none;
	font-size: 0.85rem;
	transition: all 0.3s ease;
}

.legal-links a:hover {
	color: #e74c3c;
}

@media (max-width: 768px) {
	.footer-container {
		flex-direction: column;
		text-align: center;
	}

	.footer-col h4::after {
		left: 50%;
		transform: translateX(-50%);
	}

	.social-box {
		justify-content: center;
	}

	.footer-links a {
		justify-content: center;
	}
} .contact-info {
	 margin-top:12px;
}

script:
// Newsletter Form Submission

document.getElementById("newsletterForm").addEventListener("submit", function (e) {
	e.preventDefault();
	alert("Thank you for subscribing!");
	this.reset();
});

// Scroll Reveal Animation
const scrollReveal = document.querySelectorAll(".scroll-reveal");

const revealOnScroll = () => {
	scrollReveal.forEach((element) => {
		const elementTop = element.getBoundingClientRect().top;
		const windowHeight = window.innerHeight;

		if (elementTop < windowHeight - 100) {
			element.classList.add("active");
		}
	});
};

window.addEventListener("scroll", revealOnScroll);
window.addEventListener("load", revealOnScroll);

// Smooth scroll for legal links
document.querySelectorAll('a[href^="#"]').forEach((anchor) => {
	anchor.addEventListener("click", function (e) {
		e.preventDefault();
		document.querySelector(this.getAttribute("href")).scrollIntoView({
			behavior: "smooth",
		});
	});
});
