style:
:root {
	--bg: #ffffff;
	--text: #1a1a1a;
	--primary: #7c3aed;
	--secondary: #4b5563;
	--border: #e5e7eb;
	--footer-bg: #f8fafc;
	--card-bg: #ffffff;
}

[data-theme="dark"] {
	--bg: #0f172a;
	--text: #f8fafc;
	--primary: #8b5cf6;
	--secondary: #94a3b8;
	--border: #334155;
	--footer-bg: #1e293b;
	--card-bg: #1e293b;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

body {
	background: var(--bg);
	color: var(--text);
	font-family: "Inter", sans-serif;
	min-height: 100vh;
}

.footer {
	background: var(--footer-bg);
	padding: 4rem 0;
	position: relative;
}

.card-stack {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
	gap: 2rem;
	max-width: 1280px;
	margin: 0 auto;
	padding: 0 2rem;
}

.card {
	background: var(--card-bg);
	border: 1px solid var(--border);
	border-radius: 16px;
	padding: 2rem;
	position: relative;
	transform-style: preserve-3d;
	cursor: pointer;
}

.card:hover {
	transform: translateY(-5px) rotateX(5deg) rotateY(-5deg);
	box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
}

.card-title {
	color: var(--primary);
	margin-bottom: 1rem;
	display: flex;
	align-items: center;
	gap: 0.5rem;
}

.card-content {
	color: var(--secondary);
	line-height: 1.6;
}

.dynamic-list {
	list-style: none;
	margin-top: 1rem;
}

.dynamic-list li {
	padding: 0.5rem 0;
	border-bottom: 1px solid var(--border);
}

.theme-toggle {
	position: fixed;
	bottom: 2rem;
	right: 2rem;
	width: 56px;
	height: 56px;
	background: var(--primary);
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	cursor: pointer;
	box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
}

.live-feed {
	max-width: 1280px;
	margin: 2rem auto;
	padding: 0 2rem;
}

.feed-item {
	background: var(--card-bg);
	border: 1px solid var(--border);
	border-radius: 12px;
	padding: 1rem;
	margin: 0.5rem 0;
	opacity: 0;
	transform: translateY(20px);
}

@media (max-width: 768px) {
	.card-stack {
		grid-template-columns: 1fr;
	}

	.theme-toggle {
		bottom: 1rem;
		right: 1rem;
	}
}

script:
// Dynamic Content Loading
const locations = ["New York", "London", "Tokyo", "San Francisco"];
const teamMembers = ["CEO: Alex Johnson", "CTO: Maria Chen", "CFO: Sam Wilson"];

const locationsList = document.getElementById("locationsList");
locations.forEach((location) => {
	const li = document.createElement("li");
	li.textContent = location;
	locationsList.appendChild(li);
});

const teamList = document.getElementById("teamList");
teamMembers.forEach((member) => {
	const li = document.createElement("li");
	li.textContent = member;
	teamList.appendChild(li);
});

// Theme Toggle
const themeToggle = document.getElementById("themeToggle");
let isDark = localStorage.getItem("theme") === "dark";

function toggleTheme() {
	isDark = !isDark;
	document.documentElement.setAttribute("data-theme", isDark ? "dark" : "light");
	localStorage.setItem("theme", isDark ? "dark" : "light");

themeToggle.innerHTML = isDark ? '<i class="fas fa-sun"></i>' : '<i class="fas fa-magic"></i>';
}

themeToggle.addEventListener("click", toggleTheme);
document.documentElement.setAttribute("data-theme", isDark ? "dark" : "light");

// Live Feed Animation
const feedItems = document.querySelectorAll(".feed-item");
feedItems.forEach((item, index) => {
	setTimeout(() => {
		item.style.opacity = "1";
		item.style.transform = "translateY(0)";
	}, index * 200);
});

// Card Interaction
document.querySelectorAll(".card").forEach((card) => {
	card.addEventListener("click", () => {
		card.classList.toggle("active");
	});
});

// Dynamic Year
const year = new Date().getFullYear();
const yearElement = document.createElement("div");
yearElement.style.textAlign = "center";
yearElement.style.marginTop = "2rem";
yearElement.style.color = "var(--secondary)";
yearElement.textContent = `Â© ${year} FutureTech Inc. All rights reserved.`;
document.querySelector(".footer").appendChild(yearElement);
