style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", sans-serif;
}

body {
	min-height: 200vh;
	background: #f5f5f5;
}

.footer {
	background: rgba(255, 255, 255, 0.9);
	backdrop-filter: blur(10px);
	padding: 4rem 2rem;
	border-top: 1px solid rgba(0, 0, 0, 0.1);
}

.footer-container {
	max-width: 1200px;
	margin: 0 auto;
	display: flex;
	flex-direction: column;
	gap: 3rem;
}

.footer-header {
	display: flex;
	flex-wrap: wrap;
	gap: 2rem;
	justify-content: space-between;
	align-items: center;
	padding-bottom: 2rem;
	border-bottom: 1px solid rgba(0, 0, 0, 0.1);
}

.logo img {
	height: 40px;
	filter: grayscale(1);
	transition: filter 0.3s ease;
}

.logo:hover img {
	filter: grayscale(0);
}

.footer-nav {
	display: flex;
	gap: 1.5rem;
	flex-wrap: wrap;
}

.footer-nav a {
	color: #333;
	text-decoration: none;
	position: relative;
	padding: 0.5rem 0;
}

.footer-nav a::after {
	content: "";
	position: absolute;
	bottom: 0;
	left: 0;
	width: 0;
	height: 2px;
	background: #2563eb;
	transition: width 0.3s ease;
}

.footer-nav a:hover::after {
	width: 100%;
}

.footer-content {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
	gap: 2rem;
}

.footer-section {
	display: flex;
	flex-direction: column;
	gap: 1rem;
}

.footer-section h3 {
	color: #2563eb;
	font-size: 1.1rem;
	text-transform: uppercase;
	letter-spacing: 1px;
}

.footer-section p {
	color: #666;
	line-height: 1.6;
}

.team-grid {
	display: grid;
	grid-template-columns: repeat(3, 1fr);
	gap: 1rem;
}

.team-member img {
	width: 100%;
	border-radius: 50%;
	aspect-ratio: 1/1;
	object-fit: cover;
	transition: transform 0.3s ease;
}

.team-member:hover img {
	transform: scale(1.1);
}

.social-links {
	display: flex;
	gap: 1rem;
	margin-top: 1rem;
}

.social-links a {
	width: 40px;
	height: 40px;
	background: rgba(0, 0, 0, 0.05);
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	color: #333;
	text-decoration: none;
	transition: all 0.3s ease;
}

.social-links a:hover {
	background: #2563eb;
	color: white;
}

.newsletter-form {
	display: flex;
	gap: 1rem;
	margin-top: 1rem;
}

.newsletter-input {
	flex: 1;
	padding: 0.8rem;
	border: 1px solid rgba(0, 0, 0, 0.1);
	border-radius: 8px;
	outline: none;
}

.newsletter-btn {
	padding: 0.8rem 1.5rem;
	background: #2563eb;
	color: white;
	border: none;
	border-radius: 8px;
	cursor: pointer;
	transition: opacity 0.3s ease;
}

.newsletter-btn:hover {
	opacity: 0.9;
}

.footer-bottom {
	margin-top: 3rem;
	padding-top: 2rem;
	border-top: 1px solid rgba(0, 0, 0, 0.1);
	text-align: center;
	color: #666;
}

@media (max-width: 768px) {
	.footer-header {
		flex-direction: column;
		text-align: center;
	}

	.footer-nav {
		justify-content: center;
	}

	.team-grid {
		grid-template-columns: repeat(2, 1fr);
	}
}

/* Custom Cursor */
.custom-cursor {
	position: fixed;
	width: 20px;
	height: 20px;
	border: 2px solid #2563eb;
	border-radius: 50%;
	pointer-events: none;
	transform: translate(-50%, -50%);
	transition: all 0.3s ease, width 0.3s ease, height 0.3s ease;
	z-index: 999;
}

.cursor-active {
	width: 40px;
	height: 40px;
	background: rgba(37, 99, 235, 0.1);
}

script:
document.addEventListener("mousemove", (e) => {
	cursor.style.left = e.clientX + "px";
	cursor.style.top = e.clientY + "px";
});

document.querySelectorAll("a, button, input, .team-member").forEach((element) => {
	element.addEventListener("mouseenter", () => {
		cursor.classList.add("cursor-active");
	});
	element.addEventListener("mouseleave", () => {
		cursor.classList.remove("cursor-active");
	});
});

document.getElementById("newsletterForm").addEventListener("submit", (e) => {
	e.preventDefault();
	const email = e.target.querySelector("input").value;
	alert(`Thanks for subscribing!\nWe've sent a confirmation to ${email}`);
	e.target.reset();
});

const observer = new IntersectionObserver((entries) => {
	entries.forEach((entry) => {
		if (entry.isIntersecting) {
			entry.target.style.opacity = 1;
			entry.target.style.transform = "translateY(0)";
		}
	});
});

document.querySelectorAll(".footer-section").forEach((section) => {
	section.style.opacity = 0;
	section.style.transform = "translateY(20px)";
	section.style.transition = "all 0.6s ease";
	observer.observe(section);
});

const themeToggle = document.createElement("button");
themeToggle.textContent = "ðŸŒ“";
themeToggle.style.position = "fixed";
themeToggle.style.bottom = "20px";
themeToggle.style.right = "20px";
themeToggle.style.padding = "10px";
themeToggle.style.borderRadius = "50%";
themeToggle.style.cursor = "pointer";
themeToggle.addEventListener("click", () => {
	document.body.classList.toggle("dark-theme");
});
document.body.appendChild(themeToggle);

const style = document.createElement("style");
style.textContent = `
    .dark-theme .footer {
        background: rgba(0, 0, 0, 0.9);
        color: white;
        border-color: rgba(255, 255, 255, 0.1);
    }
    .dark-theme .footer-nav a,
    .dark-theme .footer-section p {
        color: rgba(255, 255, 255, 0.8);
    }
    .dark-theme .social-links a {
        background: rgba(255, 255, 255, 0.1);
    }
`;
document.head.appendChild(style);
