style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Segoe UI", sans-serif;
}

.footer {
	background: #2a2a2a;
	color: #ffffff;
	padding: 4rem 0 2rem;
	margin-top: 3rem;
}

.footer-container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 1.5rem;
}

.footer-grid {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
	gap: 2rem;
	margin-bottom: 3rem;
}

.footer-section h3 {
	color: #00ff88;
	font-size: 1.25rem;
	margin-bottom: 1.5rem;
	position: relative;
	padding-bottom: 0.5rem;
}

.footer-section h3::after {
	content: "";
	position: absolute;
	bottom: 0;
	left: 0;
	width: 40px;
	height: 2px;
	background: currentColor;
}

.footer-links {
	list-style: none;
}

.footer-links li {
	margin-bottom: 0.8rem;
}

.footer-links a {
	color: #e0e0e0;
	text-decoration: none;
	transition: all 0.3s ease;
	display: flex;
	align-items: center;
	gap: 0.5rem;
}

.footer-links a:hover {
	color: #00ff88;
	transform: translateX(5px);
}

.social-container {
	display: flex;
	gap: 1rem;
	margin-top: 1.5rem;
}

.social-link {
	width: 40px;
	height: 40px;
	border: 2px solid #00ff88;
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	color: #00ff88;
	transition: all 0.3s ease;
	text-decoration: none;
}

.social-link:hover {
	background: #00ff88;
	color: #2a2a2a;
	transform: translateY(-3px);
}

.newsletter-form {
	margin-top: 1.5rem;
	display: flex;
	gap: 0.5rem;
}

.newsletter-input {
	flex: 1;
	padding: 0.8rem;
	border: none;
	border-radius: 4px;
	background: rgba(255, 255, 255, 0.1);
	color: white;
}

.newsletter-button {
	padding: 0.8rem 1.5rem;
	background: #00ff88;
	color: #2a2a2a;
	border: none;
	border-radius: 4px;
	cursor: pointer;
	transition: opacity 0.3s ease;
}

.newsletter-button:hover {
	opacity: 0.9;
}

.footer-bottom {
	border-top: 1px solid rgba(255, 255, 255, 0.1);
	padding-top: 2rem;
	text-align: center;
}

.legal-links {
	margin-top: 1rem;
	display: flex;
	gap: 1.5rem;
	justify-content: center;
	flex-wrap: wrap;
}

.legal-links a {
	color: #e0e0e0;
	text-decoration: none;
	transition: color 0.3s ease;
}

.legal-links a:hover {
	color: #00ff88;
}

.back-to-top {
	position: fixed;
	bottom: 30px;
	right: 30px;
	width: 40px;
	height: 40px;
	background: #00ff88;
	color: #2a2a2a;
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	cursor: pointer;
	opacity: 0;
	transition: all 0.3s ease;
	text-decoration: none;
}

.back-to-top.show {
	opacity: 1;
}

@media (max-width: 768px) {
	.footer-grid {
		grid-template-columns: 1fr;
		text-align: center;
	}

	.footer-section h3::after {
		left: 50%;
		transform: translateX(-50%);
	}

	.footer-links a {
		justify-content: center;
	}

	.social-container {
		justify-content: center;
	}

	.newsletter-form {
		flex-direction: column;
	}
}

script:
// Back to top button
const backToTop = document.getElementById("backToTop");

window.addEventListener("scroll", () => {
	backToTop.classList.toggle("show", window.scrollY > 300);
});

backToTop.addEventListener("click", (e) => {
	e.preventDefault();
	window.scrollTo({
		top: 0,
		behavior: "smooth",
	});
});

// Smooth scroll for anchor links
document.querySelectorAll('a[href^="#"]').forEach((anchor) => {
	anchor.addEventListener("click", function (e) {
		e.preventDefault();
		document.querySelector(this.getAttribute("href")).scrollIntoView({
			behavior: "smooth",
		});
	});
});

// Newsletter form submission
document.querySelector(".newsletter-form").addEventListener("submit", (e) => {
	e.preventDefault();
	const email = e.target.querySelector("input").value;
	alert(`Thank you for subscribing!\nYou'll receive updates at ${email}`);
	e.target.reset();
});
