style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Segoe UI", sans-serif;
}

.footer {
	background: #2d3436;
	color: #ffffff;
	padding: 50px 0 20px;
	margin-top: 80px;
}

.footer-container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 20px;
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
	gap: 40px;
}

.footer-section {
	margin-bottom: 30px;
}

.footer-section h3 {
	color: #74b9ff;
	margin-bottom: 25px;
	font-size: 1.3rem;
	position: relative;
	padding-bottom: 10px;
}

.footer-section h3::after {
	content: "";
	position: absolute;
	bottom: 0;
	left: 0;
	width: 40px;
	height: 2px;
	background: #74b9ff;
}

.footer-links {
	list-style: none;
}

.footer-links li {
	margin-bottom: 12px;
}

.footer-links a {
	color: #dfe6e9;
	text-decoration: none;
	transition: color 0.3s ease;
}

.footer-links a:hover {
	color: #74b9ff;
}

.contact-info li {
	margin-bottom: 15px;
	display: flex;
	align-items: center;
	gap: 10px;
	color: #dfe6e9;
}

.contact-info i {
	color: #74b9ff;
	min-width: 20px;
}

.social-media {
	display: flex;
	gap: 15px;
	margin-top: 20px;
}

.social-media a {
	color: #dfe6e9;
	font-size: 1.4rem;
	transition: color 0.3s ease;
}

.social-media a:hover {
	color: #74b9ff;
}

.newsletter-form {
	margin-top: 20px;
}

.newsletter-input {
	width: 100%;
	padding: 12px 20px;
	margin-bottom: 15px;
	border: 1px solid #636e72;
	border-radius: 4px;
	background: transparent;
	color: #fff;
}

.newsletter-btn {
	background: #74b9ff;
	color: #2d3436;
	padding: 12px 25px;
	border: none;
	border-radius: 4px;
	cursor: pointer;
	font-weight: 600;
	transition: background 0.3s ease;
}

.newsletter-btn:hover {
	background: #0984e3;
}

.footer-bottom {
	text-align: center;
	padding-top: 40px;
	margin-top: 50px;
	border-top: 1px solid #636e72;
}

.footer-bottom p {
	color: #dfe6e9;
	font-size: 0.9rem;
}

.legal-links {
	display: flex;
	justify-content: center;
	gap: 25px;
	margin-bottom: 15px;
}

.legal-links a {
	color: #dfe6e9;
	text-decoration: none;
	font-size: 0.85rem;
	transition: color 0.3s ease;
}

.legal-links a:hover {
	color: #74b9ff;
}

@media (max-width: 768px) {
	.footer-container {
		grid-template-columns: 1fr;
		text-align: center;
	}

	.contact-info li {
		justify-content: center;
	}

	.social-media {
		justify-content: center;
	}

	.footer-section h3::after {
		left: 50%;
		transform: translateX(-50%);
	}
}

script:
document.querySelector(".newsletter-form").addEventListener("submit", function (e) {
	e.preventDefault();
	const email = this.querySelector("input").value;
	if (validateEmail(email)) {
		alert("Thank you for subscribing!");
		this.reset();
	} else {
		alert("Please enter a valid email address");
	}
});

function validateEmail(email) {
	const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
	return re.test(email);
}

// Smooth scroll for anchor links
document.querySelectorAll('a[href^="#"]').forEach((anchor) => {
	anchor.addEventListener("click", function (e) {
		e.preventDefault();
		document.querySelector(this.getAttribute("href")).scrollIntoView({
			behavior: "smooth",
		});
	});
});
