style:
 * {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", sans-serif;
}

:root {
	--primary: #2f80ed;
	--secondary: #4f4f4f;
	--accent: #eb5757;
}

body {
	overflow-x: hidden;
}

.hero {
	min-height: 100vh;
	background: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)),
		url("https://images.unsplash.com/photo-1503376780353-7e6692767b70?ixlib=rb-1.2.1&auto=format&fit=crop&w=1950&q=80");
	background-size: cover;
	background-position: center;
	position: relative;
	padding: 100px 5% 60px;
}

.hero-content {
	max-width: 1200px;
	margin: 0 auto;
	color: white;
	text-align: center;
	padding-top: 120px;
}

.hero-title {
	font-size: clamp(2.5rem, 5vw, 4rem);
	line-height: 1.1;
	margin-bottom: 20px;
	animation: slideUp 1s ease-out;
}

.search-form {
	background: rgba(255, 255, 255, 0.95);
	padding: 40px;
	border-radius: 10px;
	max-width: 800px;
	margin: 40px auto;
	box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
}

.form-grid {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
	gap: 20px;
	margin-bottom: 20px;
}

.form-group {
	position: relative;
}

.form-label {
	display: block;
	text-align: left;
	color: var(--secondary);
	margin-bottom: 8px;
	font-weight: 500;
}

.form-input {
	width: 100%;
	padding: 12px 15px;
	border: 1px solid #ddd;
	border-radius: 6px;
	font-size: 16px;
}

.form-input:focus {
	outline: none;
	border-color: var(--primary);
	box-shadow: 0 0 0 3px rgba(47, 128, 237, 0.1);
}

.search-button {
	background: var(--accent);
	color: white;
	border: none;
	padding: 15px 40px;
	border-radius: 6px;
	font-size: 16px;
	font-weight: 600;
	cursor: pointer;
	transition: transform 0.3s ease;
}

.search-button:hover {
	transform: translateY(-2px);
}

.usp-grid {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
	gap: 30px;
	max-width: 1000px;
	margin: 40px auto;
}

.usp-item {
	display: flex;
	align-items: center;
	gap: 15px;
	background: rgba(255, 255, 255, 0.1);
	padding: 20px;
	border-radius: 8px;
	backdrop-filter: blur(5px);
}

.usp-icon {
	width: 40px;
	height: 40px;
	background: var(--primary);
	border-radius: 50%;
	display: grid;
	place-items: center;
	color: white;
}

@keyframes slideUp {
	from {
		opacity: 0;
		transform: translateY(30px);
	}
	to {
		opacity: 1;
		transform: translateY(0);
	}
}
