style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", sans-serif;
}

:root {
	--primary: #ff5e14;
	--secondary: #2d2d2d;
	--accent: #4caf50;
}

body {
	overflow-x: hidden;
}

.hero {
	min-height: 100vh;
	padding: 140px 5% 60px;
	background: linear-gradient(rgba(0, 0, 0, 0.4), rgba(0, 0, 0, 0.4)),
		url("https://images.unsplash.com/photo-1553440569-bcc63803a83d?ixlib=rb-1.2.1&auto=format&fit=crop&w=1920&q=80");
	background-size: cover;
	background-position: center;
	display: flex;
	align-items: center;
}

.hero-content {
	max-width: 1200px;
	margin: 0 auto;
	color: white;
}

.hero-title {
	font-size: clamp(2.5rem, 5vw, 4rem);
	line-height: 1.1;
	margin-bottom: 20px;
	animation: slideLeft 1s ease-out;
}

.hero-subtitle {
	font-size: 1.2rem;
	margin-bottom: 40px;
	opacity: 0.9;
}

.cta-buttons {
	display: flex;
	gap: 20px;
	margin-bottom: 60px;
}

.cta-button {
	padding: 15px 40px;
	border-radius: 6px;
	text-decoration: none;
	font-weight: 600;
	transition: transform 0.3s ease;
}

.primary-cta {
	background: var(--primary);
	color: white;
}

.secondary-cta {
	border: 2px solid white;
	color: white;
}

.cta-button:hover {
	transform: translateY(-3px);
}

.features-grid {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(min(250px, 100%), 1fr));
	gap: 30px;
	width: 100%;
	margin: 0 auto;
}

.feature-card {
	background: rgba(255, 255, 255, 0.1);
	padding: 25px;
	border-radius: 10px;
	backdrop-filter: blur(5px);
	display: flex;
	align-items: center;
	gap: 15px;
	transition: transform 0.3s ease;
}

.feature-card:hover {
	transform: translateY(-5px);
}

.feature-icon {
	width: 50px;
	height: 50px;
	background: var(--primary);
	border-radius: 8px;
	display: grid;
	place-items: center;
	font-size: 24px;
}

@keyframes slideLeft {
	from {
		opacity: 0;
		transform: translateX(-50px);
	}
	to {
		opacity: 1;
		transform: translateX(0);
	}
}

@media (max-width: 768px) {
	.hero {
		text-align: center;
		padding-top: 100px;
	}

	.cta-buttons {
		flex-direction: column;
	}

	.hero-text {
		margin: 0 auto;
	}
}
