style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: 'Inter', sans-serif;
}

:root {
	--primary: #2563eb;
	--secondary: #1e293b;
	--accent: #f59e0b;
}

body {
	overflow-x: hidden;
}

.hero {
	min-height: 100vh;
	display: grid;
	grid-template-columns: 1fr 1fr;
	align-items: center;
	padding: 6rem 5% 2rem;
	background: #f8fafc;
}

.hero-content {
	padding-right: 4rem;
}

.hero-title {
	font-size: clamp(2.5rem, 5vw, 3.5rem);
	line-height: 1.1;
	color: var(--secondary);
	margin-bottom: 1.5rem;
	position: relative;
}

.hero-title::after {
	content: '';
	position: absolute;
	bottom: -1rem;
	left: 0;
	width: 80px;
	height: 4px;
	background: var(--primary);
}

.hero-text {
	color: #64748b;
	line-height: 1.6;
	margin-bottom: 2.5rem;
	max-width: 500px;
}

.cta-button {
	display: inline-flex;
	align-items: center;
	padding: 1rem 2.5rem;
	background: var(--primary);
	color: white;
	border-radius: 0.5rem;
	text-decoration: none;
	font-weight: 600;
	transition: transform 0.3s ease;
	position: relative;
	overflow: hidden;
	margin-top: 1rem;
}

.cta-button::before {
	content: '';
	position: absolute;
	top: 0;
	left: -100%;
	width: 100%;
	height: 100%;
	background: linear-gradient(120deg, transparent, rgba(255, 255, 255, 0.3), transparent);
	transition: left 0.6s;
}

.cta-button:hover {
	transform: translateY(-3px);
}

.cta-button:hover::before {
	left: 100%;
}

.car-showcase {
	position: relative;
	background: url('https://images.unsplash.com/photo-1555215695-3004980ad54e?ixlib=rb-1.2.1&auto=format&fit=crop&w=1920&q=80') center/cover;
	border-radius: 1rem;
	height: 600px;
	box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
}

.price-badge {
	position: absolute;
	bottom: 2rem;
	left: 2rem;
	background: white;
	padding: 1.5rem;
	border-radius: 0.5rem;
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
}

.price-badge h3 {
	color: var(--secondary);
	margin-bottom: 0.5rem;
}

.price {
	font-size: 2rem;
	font-weight: 700;
	color: var(--primary);
}

.features-grid {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
	gap: 2rem;
	margin-top: 4rem;
}

.feature-card {
	padding: 1.5rem;
	background: white;
	border-radius: 0.5rem;
	box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
	transition: transform 0.3s ease;
}

.feature-card:hover {
	transform: translateY(-5px);
}

.feature-icon {
	width: 40px;
	height: 40px;
	background: var(--primary);
	border-radius: 50%;
	display: grid;
	place-items: center;
	margin-bottom: 1rem;
	color: white;
}

@media (max-width: 1024px) {
	.hero {
		grid-template-columns: 1fr;
		gap: 4rem;
		padding: 8rem 5% 4rem;
	}

	.hero-content {
		padding-right: 0;
		text-align: center;
	}

	.hero-title::after {
		left: 50%;
		transform: translateX(-50%);
	}

	.car-showcase {
		height: 400px;
	}
}

@media (max-width: 768px) {
	.price-badge {
		left: 50%;
		transform: translateX(-50%);
		width: 80%;
		text-align: center;
	}

	.features-grid {
		grid-template-columns: 1fr;
	}
}
