style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", sans-serif;
}

.hero {
	padding: 80px 5% 40px;
	background: linear-gradient(rgba(255, 255, 255, 0.9), rgba(255, 255, 255, 0.9)), url("https://picsum.photos/1600/900?grocery");
	background-size: cover;
	position: relative;
}

.hero-container {
	max-width: 1200px;
	margin: 0 auto;
	display: grid;
	grid-template-columns: 1fr 1fr;
	gap: 40px;
	align-items: center;
}

.hero-content {
	padding-right: 40px;
}

.hero-title {
	font-size: 3.2rem;
	color: #2c3e50;
	margin-bottom: 20px;
	line-height: 1.2;
}

.hero-subtitle {
	color: #7f8c8d;
	font-size: 1.1rem;
	margin-bottom: 35px;
	line-height: 1.6;
}

.search-container {
	position: relative;
	margin-bottom: 30px;
}

.search-input {
	width: 100%;
	padding: 16px 30px;
	border: 2px solid #ecf0f1;
	border-radius: 30px;
	font-size: 1rem;
	padding-right: 160px;
}

.search-btn {
	position: absolute;
	right: 2px;
	top: 2px;
	font-size: 1rem;
	padding: 16px;
	border: 2px solid #ecf0f1;
	background: #2ecc71;
	color: white;
	border: none;
	border-radius: 25px;
	cursor: pointer;
	display: flex;
	align-items: center;
}

.hero-highlights {
	display: flex;
	gap: 20px;
	flex-wrap: wrap;
}

.highlight-item {
	display: flex;
	align-items: center;
	gap: 10px;
	background: rgba(46, 204, 113, 0.1);
	padding: 10px 20px;
	border-radius: 20px;
	color: #2ecc71;
}

.hero-image {
	position: relative;
	border-radius: 20px;
	overflow: hidden;
	box-shadow: 0 15px 40px rgba(0, 0, 0, 0.1);
}

.promo-banner {
	position: absolute;
	bottom: 20px;
	left: 20px;
	right: 20px;
	background: rgba(255, 255, 255, 0.95);
	padding: 20px;
	border-radius: 15px;
	display: flex;
	justify-content: space-between;
	align-items: center;
}

.promo-text {
	font-size: 1.2rem;
	color: #2c3e50;
}

.promo-btn {
	border: 2px solid #ecf0f1;
	background: #2ecc71;
	color: white;
	border: none;
	border-radius: 25px;
	cursor: pointer;
	display: flex;
	align-items: center;
	padding: 16px;
}

.promo-text strong {
	color: #e74c3c;
}

.categories {
	padding: 60px 5%;
	background: #f9f9f9;
}

.categories-grid {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(180px, 1fr));
	gap: 20px;
	max-width: 1200px;
	margin: 0 auto;
}

.category-card {
	background: white;
	padding: 25px;
	border-radius: 15px;
	text-align: center;
	transition: transform 0.3s ease;
	cursor: pointer;
}

.category-card:hover {
	transform: translateY(-5px);
}

.category-icon {
	font-size: 2rem;
	color: #2ecc71;
	margin-bottom: 15px;
}

@media (max-width: 1024px) {
	.hero-container {
		grid-template-columns: 1fr;
		text-align: center;
	}

	.hero-content {
		padding-right: 0;
	}

	.hero-highlights {
		justify-content: center;
	}
}

@media (max-width: 768px) {
	.hero-title {
		font-size: 2.2rem;
	}

	.search-input {
		padding-right: 30px;
	}

	.search-btn {
		position: relative;
		width: 100%;
		margin-top: 15px;
		justify-content: center;
	}

	.promo-banner {
		flex-direction: column;
		gap: 15px;
		text-align: center;
	}
}
